-- Erstellt und befüllt die Tabelle 'church_events' mit den Daten
-- für die Kirchenjahre 2024-2025.
-- Die Daten wurden aus den .ics-Dateien extrahiert und mit dem
-- Nordelbischen Gesangbuch-Index für Hymnen- und Psalm-Nummern angereichert.

-- 1. Tabellenstruktur definieren (falls noch nicht geschehen)
CREATE TABLE IF NOT EXISTS church_events (
    id SERIAL PRIMARY KEY,
    uid VARCHAR(255) UNIQUE NOT NULL,
    summary TEXT NOT NULL,
    event_date DATE NOT NULL,
    url TEXT,
    liturgical_color VARCHAR(50),
    season VARCHAR(100),
    weekly_verse TEXT,
    weekly_verse_reference VARCHAR(100),
    psalm TEXT,
    old_testament_reading TEXT,
    epistle TEXT,
    gospel TEXT,
    sermon_text TEXT,
    hymn_1 TEXT,
    hymn_2 TEXT,
    perikopen JSONB,
    hymn1_eg INTEGER,
    hymn2_eg INTEGER,
    psalm_eg INTEGER,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

-- 2. Bestehende Daten für die relevanten Kirchenjahre löschen, um saubere Neuimporte zu ermöglichen.
DELETE FROM church_events WHERE event_date >= '2024-12-01' AND event_date <= '2027-11-28';

