-- Losungen for 2025
-- Generated from Losungen Free 2025.xml

CREATE TABLE IF NOT EXISTS losungen (
    id SERIAL PRIMARY KEY,
    date DATE UNIQUE NOT NULL,
    weekday VARCHAR(20) NOT NULL,
    holiday VARCHAR(200),
    
    -- Old Testament (Losung)
    ot_text TEXT NOT NULL,
    ot_reference VARCHAR(100) NOT NULL,
    
    -- New Testament (Lehrtext)
    nt_text TEXT NOT NULL,
    nt_reference VARCHAR(100) NOT NULL,
    
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

DELETE FROM losungen WHERE date >= '2025-01-01' AND date <= '2025-12-31';

