INSERT INTO losungen (date, weekday, holiday, ot_text, ot_reference, nt_text, nt_reference) VALUES
('2025-01-01', 'Mittwoch', 'Neujahr', 'Sage nicht: »Ich bin zu jung«, sondern du sollst gehen, wohin ich dich sende, und predigen alles, was ich dir gebiete.', 'Jeremia 1,7', 'Ich schäme mich des Evangeliums nicht.', 'Römer 1,16'),
('2025-01-02', 'Donnerstag', NULL, 'Menschenfurcht bringt zu Fall; wer sich aber auf den HERRN verlässt, wird beschützt.', 'Sprüche 29,25', 'Fürchte dich nicht, du kleine Herde! Denn es hat eurem Vater wohlgefallen, euch das Reich zu geben.', 'Lukas 12,32'),
('2025-01-03', 'Freitag', NULL, 'Sollte Gott etwas sagen und nicht tun? Sollte er etwas reden und nicht halten?', '4. Mose 23,19', 'Wer diese meine Rede hört und tut sie, der gleicht einem klugen Mann, der sein Haus auf Fels baute.', 'Matthäus 7,24'),
('2025-01-04', 'Samstag', NULL, 'In deine Hände befehle ich meinen Geist; du hast mich erlöst, HERR, du treuer Gott.', 'Psalm 31,6', 'Lasst uns freimütig hinzutreten zu dem Thron der Gnade, auf dass wir Barmherzigkeit empfangen und Gnade finden und so Hilfe erfahren zur rechten Zeit.', 'Hebräer 4,16'),
('2025-01-05', 'Sonntag', '2. Sonntag nach Weihnachten', 'Der HERR ist gütig und eine Feste zur Zeit der Not und kennt, die auf ihn trauen.', 'Nahum 1,7', 'Werft euer Vertrauen nicht weg!', 'Hebräer 10,35'),
('2025-01-06', 'Montag', 'Epiphanias', 'Er weidet mich auf einer grünen Aue und führet mich zum frischen Wasser. Er erquicket meine Seele.', 'Psalm 23,2-3', 'Ihr irrtet umher wie Schafe, doch jetzt seid ihr zurückgekehrt zum Hirten, zum Beschützer eurer Seelen.', '1. Petrus 2,25'),
('2025-01-07', 'Dienstag', NULL, 'Verachte nicht die Unterweisung durch den HERRN und sei nicht unwillig, wenn er dich ermahnt.', 'Sprüche 3,11', 'Jesus lehrte mit Vollmacht.', 'Markus 1,22'),
('2025-01-08', 'Mittwoch', NULL, 'Gott, mein Herz ist bereit, ich will singen und spielen. Wach auf, meine Seele!', 'Psalm 108,2', 'Der Gelähmte sprang auf, konnte stehen und gehen und ging mit ihnen in den Tempel, lief und sprang umher und lobte Gott.', 'Apostelgeschichte 3,8'),
('2025-01-09', 'Donnerstag', NULL, 'Gedenke des ganzen Weges, den dich der HERR, dein Gott, geleitet hat diese vierzig Jahre in der Wüste, auf dass er dich demütigte und versuchte, damit kundwürde, was in deinem Herzen wäre.', '5. Mose 8,2', 'Und alsbald trieb der Geist Jesus in die Wüste; und er war in der Wüste vierzig Tage und wurde versucht von dem Satan und war bei den Tieren, und die Engel dienten ihm.', 'Markus 1,12-13'),
('2025-01-10', 'Freitag', NULL, '/Der HERR spricht:/ Möge doch ihr Herz so bleiben, dass sie mich allezeit fürchten und meine Gebote halten, damit es ihnen und ihren Kindern gut geht, für immer!', '5. Mose 5,29', 'Ein neues Gebot gebe ich euch: dass ihr einander liebt. Wie ich euch geliebt habe, so sollt auch ihr einander lieben. Daran werden alle erkennen, dass ihr meine Jünger seid: Wenn ihr bei euch der Liebe Raum gebt.', 'Johannes 13,34-35'),
('2025-01-11', 'Samstag', NULL, 'Ich will mit ihnen einen Bund des Friedens schließen, der soll ein ewiger Bund mit ihnen sein.', 'Hesekiel 37,26', 'Gnade sei mit euch und Friede von dem, der da ist und der da war und der da kommt.', 'Offenbarung 1,4'),
('2025-01-12', 'Sonntag', '1. Sonntag nach Epiphanias', 'Hass erregt Hader; aber Liebe deckt alle Übertretungen zu.', 'Sprüche 10,12', 'Darin besteht die Liebe: nicht dass wir Gott geliebt haben, sondern dass er uns geliebt hat und gesandt seinen Sohn zur Versöhnung für unsre Sünden. Ihr Lieben, hat uns Gott so geliebt, so sollen wir uns auch untereinander lieben.', '1. Johannes 4,10-11'),
('2025-01-13', 'Montag', NULL, 'Halte meine Augen davon ab, nach Nichtigem zu schauen.', 'Psalm 119,37', 'Dein Auge ist das Licht des Leibes. Wenn dein Auge lauter ist, so ist dein ganzer Leib licht; wenn es aber böse ist, so ist auch dein Leib finster.', 'Lukas 11,34'),
('2025-01-14', 'Dienstag', NULL, 'Wer sich des Armen erbarmt, der ehrt Gott.', 'Sprüche 14,31', 'Jesus sprach zu dem jungen Mann: Willst du vollkommen sein, so geh hin, verkaufe, was du hast, und gib’s den Armen, so wirst du einen Schatz im Himmel haben; und komm und folge mir nach!', 'Matthäus 19,21'),
('2025-01-15', 'Mittwoch', NULL, 'Du sollst nicht ehebrechen.', '2. Mose 20,14', 'Der Mann soll seine Frau nicht vernachlässigen, ebenso nicht die Frau ihren Mann.', '1. Korinther 7,3'),
('2025-01-16', 'Donnerstag', NULL, 'Alle hoffärtigen Augen werden erniedrigt, und die stolzen Männer müssen sich beugen; der HERR aber wird allein hoch sein an jenem Tage.', 'Jesaja 2,11', 'Was töricht ist vor der Welt, das hat Gott erwählt, damit er die Weisen zuschanden mache; und was schwach ist vor der Welt, das hat Gott erwählt, damit er zuschanden mache, was stark ist; und was gering ist vor der Welt und was verachtet ist, das hat Gott erwählt, was nichts ist, damit er zunichtemache, was etwas ist, auf dass sich kein Mensch vor Gott rühme.', '1. Korinther 1,27-29'),
('2025-01-17', 'Freitag', NULL, 'Eines jeden Wege liegen offen vor dem HERRN.', 'Sprüche 5,21', 'Richtet nicht vor der Zeit, bis der Herr kommt, der auch ans Licht bringen wird, was im Finstern verborgen ist, und das Trachten der Herzen offenbar machen wird.', '1. Korinther 4,5'),
('2025-01-18', 'Samstag', NULL, 'Du sollst heute wissen und zu Herzen nehmen, dass der HERR Gott ist oben im Himmel und unten auf Erden und sonst keiner.', '5. Mose 4,39', 'Eure Güte lasst kund sein allen Menschen! Der Herr ist nahe! Und der Friede Gottes, der höher ist als alle Vernunft, wird eure Herzen und Sinne bewahren in Christus Jesus.', 'Philipper 4,5.7'),
('2025-01-19', 'Sonntag', '2. Sonntag nach Epiphanias', 'Mache dich auf, werde licht; denn dein Licht kommt, und die Herrlichkeit des HERRN geht auf über dir!', 'Jesaja 60,1', 'Unser Gott hat ein Herz voll Erbarmen. Darum kommt uns das Licht aus der Höhe zur Hilfe. Es leuchtet denen, die im Dunkel und im Schatten des Todes leben. Es lenkt unsere Füße auf den Weg des Friedens.', 'Lukas 1,78-79'),
('2025-01-20', 'Montag', NULL, 'Der HERR wird dein ewiges Licht und dein Gott wird dein Glanz sein.', 'Jesaja 60,19', 'Wer mich sieht, der sieht den, der mich gesandt hat. Ich bin als Licht in die Welt gekommen, auf dass, wer an mich glaubt, nicht in der Finsternis bleibe.', 'Johannes 12,45-46'),
('2025-01-21', 'Dienstag', NULL, 'Erhalte mich nach deinem Wort, dass ich lebe, und lass mich nicht zuschanden werden in meiner Hoffnung.', 'Psalm 119,116', 'Wer mein Wort hört und glaubt dem, der mich gesandt hat, der hat das ewige Leben und kommt nicht in das Gericht, sondern er ist vom Tode zum Leben hindurchgedrungen.', 'Johannes 5,24'),
('2025-01-22', 'Mittwoch', NULL, 'Jesaja sprach: Weh mir, ich vergehe! Denn ich bin unreiner Lippen und wohne unter einem Volk von unreinen Lippen.', 'Jesaja 6,5', 'Ich war früher ein Lästerer und ein Verfolger und ein Frevler; aber mir ist Barmherzigkeit widerfahren.', '1. Timotheus 1,13'),
('2025-01-23', 'Donnerstag', NULL, 'Meint ihr, dass ihr Gott täuschen werdet, wie man einen Menschen täuscht?', 'Hiob 13,9', 'Jesus sprach zu den Pharisäern: Ihr seid’s, die ihr euch selbst rechtfertigt vor den Menschen; aber Gott kennt eure Herzen.', 'Lukas 16,15'),
('2025-01-24', 'Freitag', NULL, 'Die sich halten an das Nichtige, verlassen ihre Gnade.', 'Jona 2,9', 'Tut nichts aus Eigennutz oder um eitler Ehre willen, sondern in Demut achte einer den andern höher als sich selbst.', 'Philipper 2,3'),
('2025-01-25', 'Samstag', NULL, 'Der HERR wird sich wieder über dich freuen, dir zugut, wie er sich über deine Väter gefreut hat.', '5. Mose 30,9', 'Gelobt sei der Herr, der Gott Israels! Denn er ist seinem Volk zu Hilfe gekommen und hat es erlöst. Er hat uns einen starken Retter gesandt, einen Nachkommen seines Dieners David.', 'Lukas 1,68-69'),
('2025-01-26', 'Sonntag', '3. Sonntag nach Epiphanias', 'Das geknickte Rohr wird er nicht zerbrechen, und den glimmenden Docht wird er nicht auslöschen.', 'Jesaja 42,3', 'Seht zu, dass ihr nicht eins dieser Geringen verachtet!', 'Matthäus 18,10'),
('2025-01-27', 'Montag', NULL, 'Erhöre uns nach der wunderbaren Gerechtigkeit, Gott, unser Heil.', 'Psalm 65,6', 'Das ist die Zuversicht, mit der wir vor ihm reden: Wenn wir um etwas bitten nach seinem Willen, so hört er uns.', '1. Johannes 5,14'),
('2025-01-28', 'Dienstag', NULL, 'HERR, behüte mich wie einen Augapfel im Auge.', 'Psalm 17,8', 'Der Herr ist treu; der wird euch stärken und bewahren vor dem Bösen.', '2. Thessalonicher 3,3'),
('2025-01-29', 'Mittwoch', NULL, 'Mein Leben werde wert geachtet in den Augen des HERRN, und er errette mich aus aller Not!', '1. Samuel 26,24', 'Ihr kennt die Gnade unseres Herrn Jesus Christus: Obwohl er reich ist, wurde er doch arm um euretwillen, auf dass ihr durch seine Armut reich würdet.', '2. Korinther 8,9'),
('2025-01-30', 'Donnerstag', NULL, 'Der HERR ist meine Kraft.', 'Habakuk 3,19', 'Darum werden wir nicht müde; sondern wenn auch unser äußerer Mensch verfällt, so wird doch der innere von Tag zu Tag erneuert.', '2. Korinther 4,16'),
('2025-01-31', 'Freitag', NULL, 'Herr, tue meine Lippen auf, dass mein Mund deinen Ruhm verkündige.', 'Psalm 51,17', 'Wir können’s ja nicht lassen, von dem zu reden, was wir gesehen und gehört haben.', 'Apostelgeschichte 4,20'),
('2025-02-01', 'Samstag', NULL, 'Viele, die im Staub der Erde schlafen, werden aufwachen, die einen zum ewigen Leben, die andern zu ewiger Schmach und Schande.', 'Daniel 12,2', 'Dazu ist Christus gestorben und wieder lebendig geworden, dass er über Tote und Lebende Herr sei.', 'Römer 14,9'),
('2025-02-02', 'Sonntag', 'Letzter Sonntag nach Epiphanias', 'Nach dem Wort des HERRN brachen die Israeliten auf, und nach seinem Wort lagerten sie sich.', '4. Mose 9,18', 'Durch den Glauben wurde Abraham gehorsam, als er berufen wurde, an einen Ort zu ziehen, den er erben sollte; und er zog aus und wusste nicht, wo er hinkäme.', 'Hebräer 11,8'),
('2025-02-03', 'Montag', NULL, 'Die Hand unseres Gottes war über uns, und er errettete uns.', 'Esra 8,31', 'Was für Verfolgungen habe ich nicht erlitten – und aus allen hat mich der Herr errettet!', '2. Timotheus 3,11'),
('2025-02-04', 'Dienstag', NULL, 'Ist mein Wort nicht wie ein Feuer, spricht der HERR, und wie ein Hammer, der Felsen zerschmeißt?', 'Jeremia 23,29', 'Das Wort Gottes ist lebendig und wirksam. Es ist schärfer als jedes zweischneidige Schwert und dringt durch und durch. Es durchdringt Seele und Geist, Mark und Bein. Es urteilt über die Gedanken und die Einstellung des Herzens.', 'Hebräer 4,12'),
('2025-02-05', 'Mittwoch', NULL, '/Der Knecht Gottes sprach:/ Ich bot meinen Rücken dar denen, die mich schlugen, und meine Wangen denen, die mich rauften. Mein Angesicht verbarg ich nicht vor Schmach und Speichel.', 'Jesaja 50,6', 'Niemand hat größere Liebe als wer sein Leben einsetzt für seine Freunde.', 'Johannes 15,13'),
('2025-02-06', 'Donnerstag', NULL, 'Der HERR sprach: Ich will hinfort nicht mehr die Erde verfluchen um der Menschen willen; denn das Dichten und Trachten des menschlichen Herzens ist böse von Jugend auf.', '1. Mose 8,21', 'Gott hat seinen Sohn nicht in die Welt gesandt, dass er die Welt richte, sondern dass die Welt durch ihn gerettet werde.', 'Johannes 3,17'),
('2025-02-07', 'Freitag', NULL, 'Gott, du bist mein Gott, den ich suche. Es dürstet meine Seele nach dir.', 'Psalm 63,2', 'Wen dürstet, der komme; wer da will, der nehme das Wasser des Lebens umsonst.', 'Offenbarung 22,17'),
('2025-02-08', 'Samstag', NULL, 'Er hat ein Gedächtnis gestiftet seiner Wunder, der gnädige und barmherzige HERR.', 'Psalm 111,4', 'Der Kelch des Segens, den wir segnen, ist der nicht die Gemeinschaft des Blutes Christi? Das Brot, das wir brechen, ist das nicht die Gemeinschaft des Leibes Christi?', '1. Korinther 10,16'),
('2025-02-09', 'Sonntag', '4. Sonntag vor der Passionszeit', 'Du Menschenkind, alle meine Worte, die ich dir sage, die fasse mit dem Herzen und nimm sie zu Ohren!', 'Hesekiel 3,10', 'Der Same, der auf guten Boden gesät wurde: Hier ist einer, der das Wort hört und versteht. Der trägt dann Frucht – sei es hundertfach, sei es sechzigfach, sei es dreißigfach.', 'Matthäus 13,23'),
('2025-02-10', 'Montag', NULL, 'Bei Gott ist Kraft und Einsicht. Sein ist, der da irrt und der irreführt.', 'Hiob 12,16', 'Gott will, dass alle Menschen gerettet werden und sie zur Erkenntnis der Wahrheit kommen.', '1. Timotheus 2,4'),
('2025-02-11', 'Dienstag', NULL, 'Du sollst den Geringen nicht vorziehen, aber auch den Großen nicht begünstigen.', '3. Mose 19,15', 'Haltet den Glauben an Jesus Christus, unsern Herrn der Herrlichkeit, frei von allem Ansehen der Person.', 'Jakobus 2,1'),
('2025-02-12', 'Mittwoch', NULL, 'Sie gieren alle, Klein und Groß, nach unrechtem Gewinn, und Propheten und Priester gehen alle mit Lüge um und heilen den Schaden meines Volks nur obenhin, indem sie sagen: »Friede! Friede!«, und ist doch nicht Friede.', 'Jeremia 6,13-14', 'Die Liebe sei ohne Falsch. Hasst das Böse, hängt dem Guten an.', 'Römer 12,9'),
('2025-02-13', 'Donnerstag', NULL, 'Fragt nach den Wegen der Vorzeit, welches der gute Weg sei, und wandelt darin, so werdet ihr Ruhe finden für eure Seele!', 'Jeremia 6,16', 'Wir wünschen aber sehnlichst, dass jeder von euch den gleichen Eifer für die Erfüllung der Hoffnung zeige, bis ans Ende, und dass ihr nicht träge werdet, sondern es denen gleichtut, die durch Glauben und Geduld die Verheißungen erben.', 'Hebräer 6,11-12'),
('2025-02-14', 'Freitag', NULL, 'Er war so verachtet, dass man das Angesicht vor ihm verbarg; darum haben wir ihn für nichts geachtet.', 'Jesaja 53,3', 'Sehet, welch ein Mensch!', 'Johannes 19,5'),
('2025-02-15', 'Samstag', NULL, 'Mein Gott, des Tages rufe ich, doch antwortest du nicht, und des Nachts, doch finde ich keine Ruhe.', 'Psalm 22,3', 'Christus hat in den Tagen seines irdischen Lebens Bitten und Flehen mit lautem Schreien und mit Tränen vor den gebracht, der ihn aus dem Tod erretten konnte; und er ist erhört worden, weil er Gott in Ehren hielt.', 'Hebräer 5,7'),
('2025-02-16', 'Sonntag', '3. Sonntag vor der Passionszeit – Septuagesimae', 'Er gibt dem Müden Kraft und Stärke genug dem Unvermögenden.', 'Jesaja 40,29', 'Der Herr hat zu mir gesagt: Lass dir an meiner Gnade genügen; denn meine Kraft vollendet sich in der Schwachheit.', '2. Korinther 12,9'),
('2025-02-17', 'Montag', NULL, 'Von deiner Wahrheit und von deinem Heil rede ich, HERR. Ich verhehle deine Güte und Treue nicht vor der großen Gemeinde.', 'Psalm 40,11', 'Wir fanden in unserm Gott den Mut, bei euch das Evangelium Gottes zu sagen in hartem Kampf.', '1. Thessalonicher 2,2'),
('2025-02-18', 'Dienstag', NULL, 'Du wirst erfahren, dass ich der HERR bin, an dem nicht zuschanden werden, die auf mich harren.', 'Jesaja 49,23', 'Wenn sie euch aber vor die Gerichte der Synagogen und vor die Machthaber und vor die Behörden führen, dann sorgt euch nicht, wie oder womit ihr euch verteidigen oder was ihr sagen sollt, denn der heilige Geist wird euch in jener Stunde lehren, was ihr sagen müsst.', 'Lukas 12,11-12'),
('2025-02-19', 'Mittwoch', NULL, 'Wenn der HERR nicht das Haus baut, so arbeiten umsonst, die daran bauen.', 'Psalm 127,1', 'Simon sprach: Meister, wir haben die ganze Nacht gearbeitet und nichts gefangen; aber auf dein Wort hin will ich die Netze auswerfen. Und als sie das taten, fingen sie eine große Menge Fische.', 'Lukas 5,5-6'),
('2025-02-20', 'Donnerstag', NULL, 'Der HERR wird seinem Volk Recht schaffen, und über seine Knechte wird er sich erbarmen.', '5. Mose 32,36', 'Sollte Gott nicht auch Recht schaffen seinen Auserwählten, die zu ihm Tag und Nacht rufen, und sollte er bei ihnen lange warten?', 'Lukas 18,7'),
('2025-02-21', 'Freitag', NULL, 'Weide dein Volk mit deinem Stabe.', 'Micha 7,14', 'Christus selbst hat die einen als Apostel eingesetzt, die anderen als Propheten, andere als Verkündiger des Evangeliums und wieder andere als Hirten und Lehrer, um die Heiligen auszurüsten für die Ausübung ihres Dienstes. So wird der Leib Christi aufgebaut.', 'Epheser 4,11-12'),
('2025-02-22', 'Samstag', NULL, 'Was recht ist, dem sollst du nachjagen, damit du leben kannst.', '5. Mose 16,20', 'Selig sind, die da hungert und dürstet nach der Gerechtigkeit; denn sie sollen satt werden.', 'Matthäus 5,6'),
('2025-02-23', 'Sonntag', '2. Sonntag vor der Passionszeit – Sexagesimae', '/So spricht der HERR:/ Ich will mich selbst als Wache um mein Haus lagern.', 'Sacharja 9,8', 'Jesus stand auf und bedrohte den Wind und die Wogen des Wassers, und sie legten sich und es ward eine Stille. Er sprach aber zu den Jüngern: Wo ist euer Glaube?', 'Lukas 8,24-25'),
('2025-02-24', 'Montag', NULL, 'Ihr trinkt den Wein kübelweise und verwendet die kostbarsten Parfüme; aber dass euer Land in den Untergang treibt, lässt euch kalt.', 'Amos 6,6', 'Betrinkt euch nicht; denn zu viel Wein verführt zu einem liederlichen Lebenswandel. Lasst euch lieber vom Geist Gottes erfüllen!', 'Epheser 5,18'),
('2025-02-25', 'Dienstag', NULL, 'Der HERR ist meine Macht und mein Psalm und ist mein Heil.', 'Psalm 118,14', 'Leidet jemand unter euch, der bete; ist jemand guten Mutes, der singe Psalmen.', 'Jakobus 5,13'),
('2025-02-26', 'Mittwoch', NULL, 'Wer bestimmt den Geist des HERRN, und welcher Ratgeber unterweist ihn?', 'Jesaja 40,13', 'Der Wind bläst, wo er will, und du hörst sein Sausen wohl; aber du weißt nicht, woher er kommt und wohin er fährt. So ist ein jeder, der aus dem Geist geboren ist.', 'Johannes 3,8'),
('2025-02-27', 'Donnerstag', NULL, 'Dient dem HERRN von ganzem Herzen. Und weicht nicht ab; folgt nicht denen, die nichts sind, die nichts nützen und nicht retten können, denn sie sind nichts!', '1. Samuel 12,20-21', 'Werdet nicht unverständig, sondern versteht, was der Wille des Herrn ist.', 'Epheser 5,17'),
('2025-02-28', 'Freitag', NULL, 'Du hast Menschen über unser Haupt fahren lassen, wir sind in Feuer und Wasser gekommen. Aber du hast uns herausgeführt und erquickt.', 'Psalm 66,12', 'Wir leiden Verfolgung, aber wir werden nicht verlassen. Wir werden unterdrückt, aber wir kommen nicht um. Wir tragen allezeit das Sterben Jesu an unserm Leibe, auf dass auch das Leben Jesu an unserm Leibe offenbar werde.', '2. Korinther 4,9-10'),
('2025-03-01', 'Samstag', NULL, 'Denkt an den Anfang, an das, was schon immer war: Ich bin Gott und keiner sonst, ich bin Gott, und meinesgleichen gibt es nicht.', 'Jesaja 46,9', 'Ich bin das A und das O, spricht Gott der Herr, der da ist und der da war und der da kommt, der Allmächtige.', 'Offenbarung 1,8'),
('2025-03-02', 'Sonntag', 'Sonntag vor der Passionszeit – Estomihi (Sei mir ein starker Fels! Psalm 31,3)', 'Der HERR erlöste sie, weil er sie liebte und Erbarmen mit ihnen hatte. Er nahm sie auf und trug sie allezeit von alters her.', 'Jesaja 63,9', 'Und siehe, zwei Blinde saßen am Wege; und als sie hörten, dass Jesus vorüberging, schrien sie und sprachen: Ach, Herr, du Sohn Davids, erbarme dich unser!', 'Matthäus 20,30'),
('2025-03-03', 'Montag', NULL, 'Erforsche mich, Gott, und erkenne mein Herz; prüfe mich und erkenne, wie ich’s meine. Und sieh, ob ich auf bösem Wege bin, und leite mich auf ewigem Wege.', 'Psalm 139,23-24', 'Wie eng ist die Pforte und wie schmal der Weg, der zum Leben führt.', 'Matthäus 7,14'),
('2025-03-04', 'Dienstag', NULL, 'Freue dich und sei fröhlich, du Tochter Zion! Denn siehe, ich komme und will bei dir wohnen, spricht der HERR.', 'Sacharja 2,14', 'Als Jesus in Jerusalem einzog, erregte sich die ganze Stadt und sprach: Wer ist der?', 'Matthäus 21,10'),
('2025-03-05', 'Mittwoch', NULL, 'Eure Sünden haben des HERRN Angesicht vor euch verdeckt, so dass er nicht hört.', 'Jesaja 59,2', 'Der Sünde Sold ist der Tod; die Gabe Gottes aber ist das ewige Leben in Christus Jesus, unserm Herrn.', 'Römer 6,23'),
('2025-03-06', 'Donnerstag', NULL, 'Vom Aufgang der Sonne bis zu ihrem Niedergang ist mein Name herrlich unter den Völkern, spricht der HERR Zebaoth.', 'Maleachi 1,11', 'Jesus spricht: Viele werden kommen von Osten und von Westen und mit Abraham und Isaak und Jakob im Himmelreich zu Tisch sitzen.', 'Matthäus 8,11'),
('2025-03-07', 'Freitag', NULL, 'Wo viel Worte sind, da geht’s ohne Sünde nicht ab; wer aber seine Lippen im Zaum hält, ist klug.', 'Sprüche 10,19', 'Eure Rede aber sei: Ja, ja; nein, nein. Was darüber ist, das ist vom Bösen.', 'Matthäus 5,37'),
('2025-03-08', 'Samstag', NULL, 'So spricht der HERR: Suchet mich, so werdet ihr leben.', 'Amos 5,4', 'Simon Petrus sprach zu Jesus: Herr, wohin sollen wir gehen? Du hast Worte des ewigen Lebens.', 'Johannes 6,68'),
('2025-03-09', 'Sonntag', '1. Sonntag der Passionszeit – Invokavit (Er ruft mich an, darum will ich ihn erhören. Psalm 91,15)', 'Es freue sich das Herz derer, die den HERRN suchen!', 'Psalm 105,3', 'Der Gott der Hoffnung aber erfülle euch mit aller Freude und Frieden im Glauben, dass ihr immer reicher werdet an Hoffnung durch die Kraft des Heiligen Geistes.', 'Römer 15,13'),
('2025-03-10', 'Montag', NULL, 'Ich liege und schlafe ganz mit Frieden; denn allein du, HERR, hilfst mir, dass ich sicher wohne.', 'Psalm 4,9', 'Frieden lasse ich euch, meinen Frieden gebe ich euch. Nicht gebe ich euch, wie die Welt gibt. Euer Herz erschrecke nicht und fürchte sich nicht.', 'Johannes 14,27'),
('2025-03-11', 'Dienstag', NULL, 'Josua sprach zum Volk: Ihr seid Zeugen gegen euch selbst, dass ihr euch den HERRN erwählt habt, um ihm zu dienen. Und sie sprachen: Ja!', 'Josua 24,22', 'Brüder und Schwestern, bemüht euch umso eifriger, eure Berufung und Erwählung festzumachen. Denn wenn ihr dies tut, werdet ihr niemals straucheln, und so wird euch reichlich gewährt werden der Eingang in das ewige Reich unseres Herrn und Heilands Jesus Christus.', '2. Petrus 1,10-11'),
('2025-03-12', 'Mittwoch', NULL, 'Zur selben Zeit und in jenen Tagen wird man die Missetat Israels suchen, spricht der HERR, aber es wird keine da sein, und die Sünden Judas, aber es wird keine gefunden werden; denn ich will sie vergeben.', 'Jeremia 50,20', 'Durch seine Wunden seid ihr heil geworden.', '1. Petrus 2,24'),
('2025-03-13', 'Donnerstag', NULL, 'Noah fand Gnade vor dem HERRN.', '1. Mose 6,8', 'Ihr seid ein auserwähltes Geschlecht, ein königliches Priestertum, ein heiliges Volk, ein Volk zum Eigentum, dass ihr verkündigen sollt die Wohltaten dessen, der euch berufen hat aus der Finsternis in sein wunderbares Licht.', '1. Petrus 2,9'),
('2025-03-14', 'Freitag', NULL, 'Der HERR wird König sein über alle Lande. An jenem Tag wird der HERR der einzige sein und sein Name der einzige.', 'Sacharja 14,9', 'Es ist über alle derselbe Herr, reich für alle, die ihn anrufen.', 'Römer 10,12'),
('2025-03-15', 'Samstag', NULL, 'Du sollst mit einem neuen Namen genannt werden, welchen des HERRN Mund nennen wird.', 'Jesaja 62,2', 'Wenn jemand zu Christus gehört, gehört er schon zur neuen Schöpfung. Das Alte ist vergangen, etwas Neues ist entstanden!', '2. Korinther 5,17'),
('2025-03-16', 'Sonntag', '2. Sonntag der Passionszeit – Reminiszere (Gedenke, HERR, an deine Barmherzigkeit! Psalm 25,6)', 'Ich sprach, als es mir gut ging: Ich werde nimmermehr wanken. Aber als du dein Antlitz verbargest, erschrak ich.', 'Psalm 30,7.8', 'Ihr habt nun Traurigkeit; aber ich will euch wiedersehen, und euer Herz soll sich freuen, und eure Freude soll niemand von euch nehmen.', 'Johannes 16,22'),
('2025-03-17', 'Montag', NULL, 'Wo ist solch ein Gott, wie du bist, der die Sünde vergibt und erlässt die Schuld denen, die geblieben sind als Rest seines Erbteils; der an seinem Zorn nicht ewig festhält, denn er hat Gefallen an Gnade!', 'Micha 7,18', 'Gott gedenkt der Barmherzigkeit und hilft seinem Diener Israel auf.', 'Lukas 1,54'),
('2025-03-18', 'Dienstag', NULL, 'HERR, du bist meine Stärke und Kraft und meine Zuflucht in der Not!', 'Jeremia 16,19', 'Ich weiß, an wen ich glaube, und bin gewiss, dass er bewahren kann, was mir anvertraut ist, bis an jenen Tag.', '2. Timotheus 1,12'),
('2025-03-19', 'Mittwoch', NULL, 'Hiob antwortete dem HERRN: Siehe, ich bin zu gering, was soll ich antworten? Ich will meine Hand auf meinen Mund legen.', 'Hiob 40,3-4', 'Jetzt erkenne ich stückweise; dann aber werde ich erkennen, gleichwie ich erkannt bin.', '1. Korinther 13,12'),
('2025-03-20', 'Donnerstag', NULL, 'Wenn ich dich anrufe, so erhörst du mich und gibst meiner Seele große Kraft.', 'Psalm 138,3', 'Wer da bittet, der empfängt; und wer da sucht, der findet; und wer da anklopft, dem wird aufgetan.', 'Lukas 11,10'),
('2025-03-21', 'Freitag', NULL, 'Den Demütigen wird der HERR Gnade geben.', 'Sprüche 3,34', 'Es entstand auch ein Streit unter den Jüngern, wer von ihnen als der Größte gelten könne. Jesus aber sagte zu ihnen: Die Könige herrschen über ihre Völker, und die Macht über sie haben, lassen sich als Wohltäter feiern. Unter euch aber soll es nicht so sein, sondern der Größte unter euch werde wie der Jüngste, und wer herrscht, werde wie einer, der dient.', 'Lukas 22,24-26'),
('2025-03-22', 'Samstag', NULL, 'Es kommt die Zeit, da werde ich meinen Geist ausgießen über alle Menschen.', 'Joel 3,1', 'Jesus ist nun zur Rechten Gottes erhöht und hat vom Vater die verheißene Gabe, den heiligen Geist, empfangen, den er jetzt ausgegossen hat, wie ihr seht und hört.', 'Apostelgeschichte 2,33'),
('2025-03-23', 'Sonntag', '3. Sonntag der Passionszeit – Okuli (Meine Augen sehen stets auf den HERRN. Psalm 25,15)', 'Ich will des Morgens rühmen deine Güte; denn du bist mir Schutz und Zuflucht in meiner Not.', 'Psalm 59,17', 'Gottes Hilfe habe ich erfahren bis zum heutigen Tag und stehe nun hier und bin sein Zeuge bei Klein und Groß.', 'Apostelgeschichte 26,22'),
('2025-03-24', 'Montag', NULL, 'Es ist der HERR; er tue, was ihm wohlgefällt.', '1. Samuel 3,18', 'Spricht Jesus zu ihnen: Kommt und haltet das Mahl! Niemand aber unter den Jüngern wagte, ihn zu fragen: Wer bist du? Denn sie wussten: Es ist der Herr.', 'Johannes 21,12'),
('2025-03-25', 'Dienstag', NULL, 'Noah war ein frommer Mann und ohne Tadel zu seinen Zeiten; er wandelte mit Gott.', '1. Mose 6,9', 'Wie der, der euch berufen hat, heilig ist, sollt auch ihr heilig sein in eurem ganzen Wandel.', '1. Petrus 1,15'),
('2025-03-26', 'Mittwoch', NULL, 'Als einer im Elend rief, hörte der HERR und half ihm aus allen seinen Nöten.', 'Psalm 34,7', 'Kommt her zu mir, alle, die ihr mühselig und beladen seid; ich will euch erquicken.', 'Matthäus 11,28'),
('2025-03-27', 'Donnerstag', NULL, 'Lobet Gott in den Versammlungen.', 'Psalm 68,27', 'Sie waren täglich einmütig beieinander im Tempel und brachen das Brot hier und dort in den Häusern, hielten die Mahlzeiten mit Freude und lauterem Herzen und lobten Gott und fanden Wohlwollen beim ganzen Volk.', 'Apostelgeschichte 2,46-47'),
('2025-03-28', 'Freitag', NULL, 'Der HERR Zebaoth wird Jerusalem beschirmen, wie Vögel es tun mit ihren Flügeln, er wird beschirmen und erretten, schonen und befreien.', 'Jesaja 31,5', 'Geht hin zu den verlorenen Schafen aus dem Hause Israel. Geht aber und predigt und sprecht: Das Himmelreich ist nahe herbeigekommen.', 'Matthäus 10,6-7'),
('2025-03-29', 'Samstag', NULL, 'Der HERR ist in seinem heiligen Tempel. Es sei stille vor ihm alle Welt!', 'Habakuk 2,20', 'Jesus lehrte und sprach zu ihnen: Steht nicht geschrieben: Mein Haus wird ein Bethaus heißen für alle Völker?', 'Markus 11,17'),
('2025-03-30', 'Sonntag', '4. Sonntag der Passionszeit – Lätare (Freuet euch mit Jerusalem! Jesaja 66,10)', 'Er ist der HERR, unser Gott, er richtet in aller Welt.', 'Psalm 105,7', 'Selig sind, die um der Gerechtigkeit willen verfolgt werden; denn ihrer ist das Himmelreich.', 'Matthäus 5,10'),
('2025-03-31', 'Montag', NULL, 'Alle deine Geschöpfe sollen dich preisen, HERR, alle, die zu dir gehören, sollen dir danken!', 'Psalm 145,10', 'Mit Psalmen, Lobgesängen und geistlichen Liedern singt Gott dankbar in euren Herzen.', 'Kolosser 3,16'),
('2025-04-01', 'Dienstag', NULL, 'Der HERR schafft Recht den Unterdrückten, den Hungrigen gibt er Brot.', 'Psalm 146,7', 'Der aber Samen gibt dem Sämann und Brot zur Speise, der wird auch euch Samen geben und ihn mehren und wachsen lassen die Früchte eurer Gerechtigkeit.', '2. Korinther 9,10'),
('2025-04-02', 'Mittwoch', NULL, 'Ein Sohn soll seinen Vater ehren. Bin ich nun Vater, wo ist meine Ehre?, spricht der HERR.', 'Maleachi 1,6', 'Gelobt sei Gott, der Vater unseres Herrn Jesus Christus, der Vater der Barmherzigkeit und Gott allen Trostes.', '2. Korinther 1,3'),
('2025-04-03', 'Donnerstag', NULL, 'Mein Volk tut eine zwiefache Sünde: Mich, die lebendige Quelle, verlassen sie und machen sich Zisternen, die doch rissig sind und das Wasser nicht halten.', 'Jeremia 2,13', 'Wer von dem Wasser trinkt, das ich ihm gebe, den wird in Ewigkeit nicht dürsten, sondern das Wasser, das ich ihm geben werde, das wird in ihm eine Quelle des Wassers werden, das in das ewige Leben quillt.', 'Johannes 4,14'),
('2025-04-04', 'Freitag', NULL, 'Er wird sich unser wieder erbarmen und alle unsere Sünden in die Tiefen des Meeres werfen.', 'Micha 7,19', 'Wenn wir unsere Schuld eingestehen, ist Gott treu und gerecht: Er vergibt uns die Schuld und reinigt uns von allem Unrecht, das wir begangen haben.', '1. Johannes 1,9'),
('2025-04-05', 'Samstag', NULL, 'Der HERR macht arm und macht reich; er erniedrigt und erhöht.', '1. Samuel 2,7', 'Ich sage jedem Einzelnen von euch: Überschätzt euch nicht und traut euch nicht mehr zu, als angemessen ist. Strebt lieber nach nüchterner Selbsteinschätzung. Und zwar jeder so, wie Gott es für ihn bestimmt hat – und wie es dem Maßstab des Glaubens entspricht.', 'Römer 12,3'),
('2025-04-06', 'Sonntag', '5. Sonntag der Passionszeit – Judika (Schaffe mir Recht, Gott! Psalm 43,1)', 'Die im Elend ohne Obdach sind, führe ins Haus!', 'Jesaja 58,7', 'Der König wird den Gerechten zur Antwort geben: Amen, ich sage euch: Was ihr einem dieser meiner geringsten Brüder getan habt, das habt ihr mir getan.', 'Matthäus 25,40'),
('2025-04-07', 'Montag', NULL, 'Der HERR schafft Gerechtigkeit und Recht allen, die Unrecht leiden.', 'Psalm 103,6', 'Wer nun weiß, Gutes zu tun, und tut’s nicht, dem ist’s Sünde.', 'Jakobus 4,17'),
('2025-04-08', 'Dienstag', NULL, 'Der HERR, euer Gott, ist gnädig und barmherzig und wird sein Angesicht nicht von euch wenden, wenn ihr euch zu ihm bekehrt.', '2. Chronik 30,9', 'Ich habe für dich gebeten, dass dein Glaube nicht aufhöre. Und wenn du dann umkehrst, so stärke deine Brüder.', 'Lukas 22,32'),
('2025-04-09', 'Mittwoch', NULL, 'Die auf den HERRN sehen, werden strahlen vor Freude, und ihr Angesicht soll nicht schamrot werden.', 'Psalm 34,6', 'Ein Beispiel habe ich euch gegeben, damit ihr tut, wie ich euch getan habe.', 'Johannes 13,15'),
('2025-04-10', 'Donnerstag', NULL, 'In deiner Hand, HERR, steht es, jedermann groß und stark zu machen.', '1. Chronik 29,12', 'Beugt euch also demütig unter die starke Hand Gottes, damit er euch zu seiner Zeit erhöhe.', '1. Petrus 5,6'),
('2025-04-11', 'Freitag', NULL, 'Dein Reich ist ein ewiges Reich, und deine Herrschaft währet für und für.', 'Psalm 145,13', 'Jedes Geschöpf, das im Himmel ist und auf Erden und unter der Erde und auf dem Meer und alles, was darin ist, hörte ich sagen: Dem, der auf dem Thron sitzt, und dem Lamm sei Lob und Ehre und Preis und Gewalt von Ewigkeit zu Ewigkeit!', 'Offenbarung 5,13'),
('2025-04-12', 'Samstag', NULL, 'Josua fiel auf sein Angesicht zur Erde nieder, betete an und sprach: Was sagt mein Herr seinem Knecht?', 'Josua 5,14', 'Saul sprach: Herr, wer bist du? Der Herr sprach: Ich bin Jesus, den du verfolgst; steh nun auf und stell dich auf deine Füße. Denn dazu bin ich dir erschienen, um dich zu erwählen zum Diener und zum Zeugen für das, was du gesehen hast und wie ich dir erscheinen will.', 'Apostelgeschichte 26,15-16'),
('2025-04-13', 'Sonntag', '6. Sonntag der Passionszeit – Palmsonntag', 'Es übervorteile keiner seinen Nächsten, sondern fürchte dich vor deinem Gott.', '3. Mose 25,17', 'Seid so unter euch gesinnt, wie es der Gemeinschaft in Christus Jesus entspricht.', 'Philipper 2,5'),
('2025-04-14', 'Montag', NULL, 'Die Erde ist voll der Güte des HERRN.', 'Psalm 33,5', 'Gott hat sich selbst nicht unbezeugt gelassen, hat viel Gutes getan und euch vom Himmel Regen und fruchtbare Zeiten gegeben, hat euch ernährt und eure Herzen mit Freude erfüllt.', 'Apostelgeschichte 14,17'),
('2025-04-15', 'Dienstag', NULL, 'Was können die Weisen Weises lehren, wenn sie des HERRN Wort verwerfen?', 'Jeremia 8,9', 'Wo sind die Klugen? Wo sind die Schriftgelehrten? Wo sind die Weisen dieser Welt? Hat nicht Gott die Weisheit der Welt zur Torheit gemacht?', '1. Korinther 1,20'),
('2025-04-16', 'Mittwoch', NULL, 'Ein Mensch ist in seinem Leben wie Gras, er blüht wie eine Blume auf dem Felde; wenn der Wind darüber geht, so ist sie nimmer da. Die Gnade aber des HERRN währt von Ewigkeit zu Ewigkeit.', 'Psalm 103,15-16.17', 'Denn was jetzt vergänglich ist, muss mit Unvergänglichkeit bekleidet werden, und was jetzt sterblich ist, muss mit Unsterblichkeit bekleidet werden.', '1. Korinther 15,53'),
('2025-04-17', 'Donnerstag', 'Gründonnerstag', 'Dennoch bleibe ich stets an dir; denn du hältst mich bei meiner rechten Hand.', 'Psalm 73,23', 'Vater, willst du, so nimm diesen Kelch von mir; doch nicht mein, sondern dein Wille geschehe! Es erschien ihm aber ein Engel vom Himmel und stärkte ihn.', 'Lukas 22,42-43'),
('2025-04-18', 'Freitag', 'Karfreitag', 'Er heißt Wunder-Rat, Gott-Held, Ewig-Vater, Friede-Fürst.', 'Jesaja 9,5', 'Gott war in Christus und versöhnte die Welt mit ihm selber und rechnete ihnen ihre Sünden nicht zu und hat unter uns aufgerichtet das Wort von der Versöhnung.', '2. Korinther 5,19'),
('2025-04-19', 'Samstag', NULL, 'Der HERR hat mich gesandt, zu verkündigen ein gnädiges Jahr des HERRN und einen Tag der Rache unseres Gottes.', 'Jesaja 61,1.2', 'Jesus sagte zu dem Verbrecher: Amen, ich sage dir: Heute noch wirst du mit mir im Paradies sein.', 'Lukas 23,43'),
('2025-04-20', 'Sonntag', 'Osterfest', 'Als Hiskia den Brief gelesen hatte, ging er hinauf zum Hause des HERRN und breitete ihn aus vor dem HERRN.', '2. Könige 19,14', 'Jesus nahm mit sich Petrus, Johannes und Jakobus und ging auf einen Berg, um zu beten.', 'Lukas 9,28'),
('2025-04-21', 'Montag', 'Ostermontag', 'Wenn ihr euch von ganzem Herzen zu dem HERRN bekehren wollt, so tut von euch die fremden Götter.', '1. Samuel 7,3', 'Für uns gilt: Nur einer ist Gott – der Vater. Alles hat in ihm seinen Ursprung, und er ist das Ziel unseres Lebens. Und nur einer ist der Herr: Jesus Christus. Alles ist durch ihn entstanden, und durch ihn haben wir das Leben.', '1. Korinther 8,6'),
('2025-04-22', 'Dienstag', NULL, 'Wir gingen alle in die Irre wie Schafe, ein jeder sah auf seinen Weg. Aber der HERR warf unser aller Sünde auf ihn.', 'Jesaja 53,6', 'Christus hat unsre Sünden selbst hinaufgetragen an seinem Leibe auf das Holz, damit wir, den Sünden abgestorben, der Gerechtigkeit leben.', '1. Petrus 2,24'),
('2025-04-23', 'Mittwoch', NULL, 'Ich bin der allmächtige Gott; wandle vor mir und sei fromm.', '1. Mose 17,1', 'Jesus spricht zu Thomas: Reiche deinen Finger her und sieh meine Hände, und reiche deine Hand her und lege sie in meine Seite, und sei nicht ungläubig, sondern gläubig!', 'Johannes 20,27'),
('2025-04-24', 'Donnerstag', NULL, 'Mein Herz freut sich, dass du so gerne hilfst.', 'Psalm 13,6', 'Sie wunderten sich über die Maßen und sprachen: Er hat alles wohl gemacht; die Tauben macht er hören und die Sprachlosen reden.', 'Markus 7,37'),
('2025-04-25', 'Freitag', NULL, 'Die Israeliten sprachen zu Samuel: Lass nicht ab, für uns zu schreien zu dem HERRN, unserm Gott, dass er uns helfe.', '1. Samuel 7,8', 'Ich weiß: Alles, was ich jetzt durchmache, wird zuletzt zu meiner Rettung führen. Darin unterstützen mich eure Gebete und der Geist, durch den Jesus Christus mir beisteht.', 'Philipper 1,19'),
('2025-04-26', 'Samstag', NULL, 'Wer Geld liebt, wird vom Geld niemals satt, und wer Reichtum liebt, wird keinen Nutzen davon haben.', 'Prediger 5,9', 'Wir haben nichts in die Welt gebracht; darum können wir auch nichts hinausbringen. Wenn wir aber Nahrung und Kleider haben, so wollen wir uns damit begnügen.', '1. Timotheus 6,7-8'),
('2025-04-27', 'Sonntag', '1. Sonntag nach Ostern – Quasimodogeniti (Wie die neugeborenen Kindlein. 1.Petrus 2,2)', 'Das ist mir lieb, dass der HERR meine Stimme und mein Flehen hört.', 'Psalm 116,1', 'Der Schuldner warf sich vor ihm nieder und bat: Hab doch Geduld mit mir! Ich will dir ja alles zurückzahlen. Da bekam der Herr Mitleid; er gab ihn frei und erließ ihm auch noch die ganze Schuld.', 'Matthäus 18,26-27'),
('2025-04-28', 'Montag', NULL, 'Eile, mir beizustehen, Herr, meine Hilfe!', 'Psalm 38,23', 'Jesus fragte den Blinden: Was soll ich für dich tun? Er sagte: Herr, mach, dass ich wieder sehen kann! Und Jesus sagte zu ihm: Du sollst wieder sehen! Dein Glaube hat dich gerettet. Und auf der Stelle sah er wieder, und er folgte ihm und pries Gott.', 'Lukas 18,40-43'),
('2025-04-29', 'Dienstag', NULL, 'Wer seinen Nächsten verachtet, versündigt sich; aber wohl dem, der sich der Elenden erbarmt!', 'Sprüche 14,21', 'Wenn ihr alles getan habt, was Gott euch befohlen hat, dann sagt: Wir sind Diener, weiter nichts; wir haben nur getan, was uns aufgetragen war.', 'Lukas 17,10'),
('2025-04-30', 'Mittwoch', NULL, 'Dein, HERR, ist die Größe und die Macht und die Herrlichkeit und der Ruhm und die Hoheit. Denn alles im Himmel und auf Erden ist dein.', '1. Chronik 29,11', 'Gott hat uns wissen lassen das Geheimnis seines Willens nach seinem Ratschluss, den er zuvor in Christus gefasst hatte, um die Fülle der Zeiten heraufzuführen, auf dass alles zusammengefasst würde in Christus, was im Himmel und auf Erden ist, durch ihn.', 'Epheser 1,9-10'),
('2025-05-01', 'Donnerstag', NULL, 'All sein Tun ist Wahrheit, und seine Wege sind recht, und wer stolz einherschreitet, den kann er demütigen.', 'Daniel 4,34', 'Der Hauptmann aber, der dabeistand, Jesus gegenüber, und sah, dass er so verschied, sprach: Wahrlich, dieser Mensch ist Gottes Sohn gewesen!', 'Markus 15,39'),
('2025-05-02', 'Freitag', NULL, 'Ich will dich nicht verlassen noch von dir weichen.', 'Josua 1,5', 'Siehe, da tat sich Jesus der Himmel auf, und er sah den Geist Gottes wie eine Taube herabfahren und über sich kommen.', 'Matthäus 3,16'),
('2025-05-03', 'Samstag', NULL, 'HERR, neige mein Herz zu deinen Zeugnissen und nicht zur Habsucht.', 'Psalm 119,36', 'Sei du selbst ihnen ein Vorbild im Tun des Guten und, was die Lehre betrifft, ein Beispiel deines unbestechlichen Urteils, von allen geachtet und untadelig in der Verkündigung des gesunden Wortes, so dass dein Gegenspieler beschämt wird und nichts Schlechtes mehr über uns zu sagen weiß.', 'Titus 2,7-8'),
('2025-05-04', 'Sonntag', '2. Sonntag nach Ostern – Miserikordias Domini (Die Erde ist voll der Güte des HERRN. Psalm 33,5)', 'Der HERR ist meine Stärke und mein Lobgesang und ist mein Heil.', '2. Mose 15,2', 'Gewaltiges hat er vollbracht mit seinem Arm, zerstreut hat er, die hochmütig sind in ihrem Herzen.', 'Lukas 1,51'),
('2025-05-05', 'Montag', NULL, 'Der HERR verstößt nicht ewig; sondern er betrübt wohl und erbarmt sich wieder nach seiner großen Güte.', 'Klagelieder 3,31-32', 'Zacharias forderte eine kleine Tafel und schrieb: Er heißt Johannes. Und sie wunderten sich alle. Und sogleich wurde sein Mund und seine Zunge aufgetan, und er redete und lobte Gott.', 'Lukas 1,63-64'),
('2025-05-06', 'Dienstag', NULL, 'So hört nun, ihr, die ihr ferne seid, was ich getan habe, und die ihr nahe seid, erkennt meine Stärke!', 'Jesaja 33,13', 'Jesus spricht zu Marta: Habe ich dir nicht gesagt: Wenn du glaubst, wirst du die Herrlichkeit Gottes sehen?', 'Johannes 11,40'),
('2025-05-07', 'Mittwoch', NULL, 'Der HERR antwortete Hiob: Wo warst du, als ich die Erde gründete und zum Meer sprach: »Bis hierher sollst du kommen und nicht weiter; hier sollen sich legen deine stolzen Wellen!«?', 'Hiob 38,4.11', 'Jesus stand auf und bedrohte den Wind und das Meer; und es ward eine große Stille. Die Menschen aber verwunderten sich und sprachen: Was ist das für ein Mann, dass ihm Wind und Meer gehorsam sind?', 'Matthäus 8,26-27'),
('2025-05-08', 'Donnerstag', NULL, 'Gott verkündigte euch seinen Bund, den er euch gebot zu halten, nämlich die Zehn Worte, und schrieb sie auf zwei steinerne Tafeln.', '5. Mose 4,13', 'Ihr sollt nicht meinen, dass ich gekommen bin, das Gesetz oder die Propheten aufzulösen; ich bin nicht gekommen aufzulösen, sondern zu erfüllen.', 'Matthäus 5,17'),
('2025-05-09', 'Freitag', NULL, 'Dein Volk spricht: »Der HERR handelt nicht recht«, während doch sie nicht recht handeln.', 'Hesekiel 33,17', 'Geht es bei Gott etwa ungerecht zu? Gewiss nicht!', 'Römer 9,14'),
('2025-05-10', 'Samstag', NULL, 'Bin ich nur ein Gott, der nahe ist, spricht der HERR, und nicht auch ein Gott, der ferne ist?', 'Jeremia 23,23', 'Zu der neunten Stunde rief Jesus laut: Eli, Eli, lama asabtani? Das heißt übersetzt: Mein Gott, mein Gott, warum hast du mich verlassen?', 'Markus 15,34'),
('2025-05-11', 'Sonntag', '3. Sonntag nach Ostern – Jubilate (Jauchzet Gott, alle Lande! Psalm 66,1)', 'Du sollst nicht stehlen.', '2. Mose 20,15', 'Wer stiehlt, stehle nicht mehr, sondern arbeite und tue etwas Rechtes mit seinen Händen, damit er etwas hat, das er dem Notleidenden geben kann.', 'Epheser 4,28'),
('2025-05-12', 'Montag', NULL, 'Wenn ihr mich von ganzem Herzen suchen werdet, so will ich mich von euch finden lassen, spricht der HERR.', 'Jeremia 29,13-14', 'Und siehe, eine Frau, die seit zwölf Jahren den Blutfluss hatte, trat von hinten an ihn heran und berührte den Saum seines Gewandes. Denn sie sprach bei sich selbst: Wenn ich nur sein Gewand berühre, so werde ich gesund. Da wandte sich Jesus um und sah sie und sprach: Sei getrost, meine Tochter, dein Glaube hat dir geholfen. Und die Frau wurde gesund zu derselben Stunde.', 'Matthäus 9,20-22'),
('2025-05-13', 'Dienstag', NULL, 'Ich freue mich über dein Wort wie einer, der große Beute macht.', 'Psalm 119,162', 'Das Himmelreich gleicht einem Schatz, verborgen im Acker, den ein Mensch fand und verbarg; und in seiner Freude geht er hin und verkauft alles, was er hat, und kauft den Acker.', 'Matthäus 13,44'),
('2025-05-14', 'Mittwoch', NULL, 'Nähme ich Flügel der Morgenröte und bliebe am äußersten Meer, so würde auch dort deine Hand mich führen und deine Rechte mich halten.', 'Psalm 139,9-10', 'Letzte Nacht stand plötzlich ein Engel vor mir – ein Engel des Gottes, dem ich gehöre und dem ich diene. Er sagte: »Hab keine Angst, Paulus! Du musst noch vor den Kaiser treten. Deinetwegen schenkt Gott auch all denen das Leben, die mit dir auf dem Schiff sind.«', 'Apostelgeschichte 27,23-24'),
('2025-05-15', 'Donnerstag', NULL, 'Ein geängstetes, zerschlagenes Herz wirst du, Gott, nicht verachten.', 'Psalm 51,19', 'Der Zöllner stand ferne, wollte auch die Augen nicht aufheben zum Himmel, sondern schlug an seine Brust und sprach: Gott, sei mir Sünder gnädig! Ich sage euch: Dieser ging gerechtfertigt hinab in sein Haus.', 'Lukas 18,13-14'),
('2025-05-16', 'Freitag', NULL, 'Ich will hoffen auf den HERRN, der sein Antlitz verborgen hat vor dem Hause Jakob.', 'Jesaja 8,17', 'Sie sind doch Israel, das von Gott erwählte Volk. Ihnen gehört das Vorrecht, Kinder Gottes zu sein. Ihnen offenbarte er seine Herrlichkeit. Mit ihnen hat er wiederholt seinen Bund geschlossen. Ihnen hat er sein Gesetz gegeben und die Ordnungen für den Opferdienst zu seiner Verehrung. Ihnen hat er das künftige Heil versprochen.', 'Römer 9,4'),
('2025-05-17', 'Samstag', NULL, 'Die Gnade des HERRN währt von Ewigkeit zu Ewigkeit über denen, die ihn fürchten, und seine Gerechtigkeit auf Kindeskind bei denen, die seinen Bund halten.', 'Psalm 103,17-18', 'Weil du mein Wort bewahrt hast, das dir die Kraft gibt, auszuharren, werde auch ich dich bewahren in der Stunde der Versuchung, die über den ganzen Erdkreis kommen wird, die Erdenbewohner zu versuchen.', 'Offenbarung 3,10'),
('2025-05-18', 'Sonntag', '4. Sonntag nach Ostern – Kantate (Singet dem HERRN ein neues Lied! Psalm 98,1)', 'Der HERR kennt die Gedanken der Menschen: Sie sind nur ein Hauch!', 'Psalm 94,11', 'Niemand betrüge sich selbst. Wer unter euch meint, weise zu sein in dieser Welt, der werde ein Narr, dass er weise werde. Denn die Weisheit dieser Welt ist Torheit bei Gott.', '1. Korinther 3,18-19'),
('2025-05-19', 'Montag', NULL, 'Wir wissen nicht, was wir tun sollen, sondern unsere Augen sehen nach dir.', '2. Chronik 20,12', 'Jesus sprach zu den zweiundsiebzig Jüngern: Wenn ihr in ein Haus kommt, sprecht zuerst: Friede sei diesem Hause!', 'Lukas 10,2.5'),
('2025-05-20', 'Dienstag', NULL, 'So spricht der HERR: Ich habe dein Gebet gehört und deine Tränen gesehen. Siehe, ich will dich gesund machen.', '2. Könige 20,5', 'Der königliche Beamte sprach zu ihm: Herr, komm herab, ehe mein Kind stirbt! Jesus spricht zu ihm: Geh hin, dein Sohn lebt! Der Mann glaubte dem Wort, das Jesus zu ihm sagte, und ging hin. Und während er noch hinabging, begegneten ihm seine Knechte und sagten: Dein Kind lebt.', 'Johannes 4,49-51'),
('2025-05-21', 'Mittwoch', NULL, 'Fürchte dich nicht, Zion! Lass deine Hände nicht sinken! Denn der HERR, dein Gott, ist bei dir, ein starker Heiland.', 'Zefanja 3,16-17', 'Erbarmt euch derer, die zweifeln.', 'Judas 1,22'),
('2025-05-22', 'Donnerstag', NULL, 'HERR, lass den Geringen nicht beschämt davongehen.', 'Psalm 74,21', 'Die kanaanäische Frau fiel vor Jesus nieder und sprach: Herr, hilf mir! Aber er antwortete und sprach: Es ist nicht recht, dass man den Kindern ihr Brot nehme und werfe es vor die Hunde. Sie sprach: Ja, Herr; aber doch essen die Hunde von den Brosamen, die vom Tisch ihrer Herren fallen. Da antwortete Jesus und sprach zu ihr: Frau, dein Glaube ist groß. Dir geschehe, wie du willst! Und ihre Tochter wurde gesund zu derselben Stunde.', 'Matthäus 15,25-28'),
('2025-05-23', 'Freitag', NULL, 'Ich will sie sammeln von den Enden der Erde, unter ihnen Blinde und Lahme, Schwangere und junge Mütter, dass sie als große Gemeinde wieder hierher kommen sollen.', 'Jeremia 31,8', 'Da wurde der Hausherr zornig und sprach zu seinem Knecht: Geh schnell hinaus auf die Straßen und Gassen der Stadt und führe die Armen und Verkrüppelten und Blinden und Lahmen herein.', 'Lukas 14,21'),
('2025-05-24', 'Samstag', NULL, 'Du allein kennst das Herz aller Menschenkinder.', '1. Könige 8,39', 'Jesus sah Nathanael kommen und sagt von ihm: Siehe, ein rechter Israelit, in dem kein Falsch ist. Nathanael spricht zu ihm: Woher kennst du mich?', 'Johannes 1,47-48'),
('2025-05-25', 'Sonntag', '5. Sonntag nach Ostern – Rogate (Betet!)', 'Du sollst dein Herz nicht verhärten und deine Hand nicht zuhalten gegenüber deinem armen Bruder.', '5. Mose 15,7', 'Meine Brüder und Schwestern! Was nützt es, wenn jemand behauptet zu glauben, sich der Glaube aber nicht in Taten zeigt?', 'Jakobus 2,14'),
('2025-05-26', 'Montag', NULL, 'Es soll nicht durch Heer oder Kraft, sondern durch meinen Geist geschehen, spricht der HERR Zebaoth.', 'Sacharja 4,6', 'Der Engel sprach zu Maria: Der Heilige Geist wird über dich kommen, und die Kraft des Höchsten wird dich überschatten; darum wird auch das Heilige, das geboren wird, Gottes Sohn genannt werden.', 'Lukas 1,35'),
('2025-05-27', 'Dienstag', NULL, 'Wo ist ein Fels außer unserm Gott?', 'Psalm 18,32', 'Wir dachten bei uns selbst, zum Tode verurteilt zu sein. Das geschah aber, damit wir unser Vertrauen nicht auf uns selbst setzten, sondern auf Gott, der die Toten auferweckt, der uns aus solcher Todesnot errettet hat und erretten wird. Auf ihn hoffen wir, er werde uns auch hinfort erretten.', '2. Korinther 1,9-10'),
('2025-05-28', 'Mittwoch', NULL, 'Zur letzten Zeit wird kein Volk wider das andere das Schwert erheben, und sie werden hinfort nicht mehr lernen, Krieg zu führen.', 'Jesaja 2,2.4', 'Jagt dem Frieden nach mit jedermann und der Heiligung, ohne die niemand den Herrn sehen wird.', 'Hebräer 12,14'),
('2025-05-29', 'Donnerstag', 'Christi Himmelfahrt', '/Der HERR spricht:/ Ich will Frieden geben in eurem Lande, dass ihr schlaft und euch niemand aufschrecke.', '3. Mose 26,6', 'Ich treibe die bösen Geister mit dem Finger Gottes aus, und daran könnt ihr sehen, dass Gott schon angefangen hat, mitten unter euch seine Herrschaft aufzurichten.', 'Lukas 11,20'),
('2025-05-30', 'Freitag', NULL, 'Fröhlich lass sein in dir, die deinen Namen lieben!', 'Psalm 5,12', 'Die Apostel gingen aber fröhlich von dem Hohen Rat fort, weil sie würdig gewesen waren, um Seines Namens willen Schmach zu leiden, und sie hörten nicht auf, alle Tage im Tempel und hier und dort in den Häusern zu lehren und zu predigen das Evangelium von Jesus Christus.', 'Apostelgeschichte 5,41-42'),
('2025-05-31', 'Samstag', NULL, 'Wohl dem, der barmherzig ist und gerne leiht und das Seine tut, wie es recht ist!', 'Psalm 112,5', 'Die Reichen sollen Gutes tun, reich werden an guten Werken, freigebig sein und ihren Sinn auf das Gemeinwohl richten. So verschaffen sie sich eine gute Grundlage für die Zukunft, die dazu dient, das wahre Leben zu gewinnen.', '1. Timotheus 6,18-19'),
('2025-06-01', 'Sonntag', '6. Sonntag nach Ostern – Exaudi (HERR, höre meine Stimme! Psalm 27,7)', 'Hanna betete: Ach, HERR Zebaot, sieh das Elend deiner Magd an! Denk doch an mich und vergiss deine Magd nicht! Schenk deiner Magd einen Sohn! Dann will ich ihn dem HERRN überlassen sein ganzes Leben lang.', '1. Samuel 1,11', 'Für Elisabeth kam die Zeit, dass sie gebären sollte; und sie gebar einen Sohn. Und ihre Nachbarn und Verwandten hörten, dass der Herr große Barmherzigkeit an ihr getan hatte, und freuten sich mit ihr.', 'Lukas 1,57-58'),
('2025-06-02', 'Montag', NULL, '/Jeremia sprach:/ Mich jammert von Herzen, dass die Tochter meines Volks so zerschlagen ist. Ist denn keine Salbe in Gilead oder ist kein Arzt da?', 'Jeremia 8,21.22', 'Jesus sprach zu Zachäus: Heute ist diesem Hause Heil widerfahren, denn auch er ist ein Sohn Abrahams. Denn der Menschensohn ist gekommen, zu suchen und selig zu machen, was verloren ist.', 'Lukas 19,9-10'),
('2025-06-03', 'Dienstag', NULL, 'Heilig, heilig, heilig ist der HERR Zebaoth, alle Lande sind seiner Ehre voll!', 'Jesaja 6,3', 'Der Engel sprach mit großer Stimme: Fürchtet Gott und gebt ihm die Ehre; denn die Stunde seines Gerichts ist gekommen! Und betet an den, der gemacht hat Himmel und Erde und Meer und die Wasserquellen!', 'Offenbarung 14,7'),
('2025-06-04', 'Mittwoch', NULL, 'Du bist mein Vater, mein Gott und der Hort meines Heils.', 'Psalm 89,27', 'Unser tägliches Brot gib uns heute.', 'Matthäus 6,11'),
('2025-06-05', 'Donnerstag', NULL, '/Gott spricht zum Frevler:/ Was redest du von meinen Geboten und nimmst meinen Bund in deinen Mund, da du doch Zucht hassest und wirfst meine Worte hinter dich?', 'Psalm 50,16-17', 'Es werden nicht alle, die zu mir sagen: Herr, Herr!, in das Himmelreich kommen, sondern die den Willen tun meines Vaters im Himmel.', 'Matthäus 7,21'),
('2025-06-06', 'Freitag', NULL, 'Um Jerusalem her sind Berge, und der HERR ist um sein Volk her von nun an bis in Ewigkeit.', 'Psalm 125,2', 'Ich bitte nicht, dass du sie aus der Welt nimmst, sondern dass du sie bewahrst vor dem Bösen.', 'Johannes 17,15'),
('2025-06-07', 'Samstag', NULL, 'HERR, in deiner Hand ist Kraft und Macht, und es ist niemand, der dir zu widerstehen vermag.', '2. Chronik 20,6', 'Sie trieben Jesus aus Nazaret hinaus – bis an den Abhang des Berges, auf dem ihre Stadt gebaut war. Dort wollten sie ihn hinunterstürzen. Aber Jesus ging mitten durch die Menge hindurch und zog weiter.', 'Lukas 4,29-30'),
('2025-06-08', 'Sonntag', 'Pfingstfest', 'HERR, du machst alles lebendig, und das himmlische Heer betet dich an.', 'Nehemia 9,6', 'Und als sie gebetet hatten, erbebte die Stätte, wo sie versammelt waren; und sie wurden alle vom Heiligen Geist erfüllt und redeten das Wort Gottes mit Freimut.', 'Apostelgeschichte 4,31'),
('2025-06-09', 'Montag', 'Pfingstmontag', 'Der Gerechte muss viel leiden, aber aus alledem hilft ihm der HERR.', 'Psalm 34,20', 'Und wenn man euch abführt und vor Gericht stellt, dann sorgt euch nicht im Voraus, was ihr reden sollt, sondern was euch in jener Stunde eingegeben wird, das redet. Denn nicht ihr seid es, die reden, sondern der heilige Geist.', 'Markus 13,11'),
('2025-06-10', 'Dienstag', NULL, 'Der HERR ist mein Fels und meine Burg und mein Erretter.', '2. Samuel 22,2', 'Der Herr stand mir bei und gab mir Kraft. Denn die Verkündigung seiner Botschaft sollte durch mich ihr Ziel erreichen: Alle Völker sollten sie hören. Und ich wurde aus dem Rachen des Löwen gerettet.', '2. Timotheus 4,17'),
('2025-06-11', 'Mittwoch', NULL, 'Sechs Tage sollst du arbeiten und alle deine Werke tun. Aber am siebenten Tage ist der Sabbat des HERRN, deines Gottes. Da sollst du keine Arbeit tun.', '2. Mose 20,9.10', 'Jesus sprach zu ihnen: Was ist am Sabbat erlaubt: Gutes tun oder Böses tun, Leben retten oder töten?', 'Markus 3,4'),
('2025-06-12', 'Donnerstag', NULL, 'Dem HERRN, eurem Gott, sollt ihr folgen und ihn fürchten und seine Gebote halten und seiner Stimme gehorchen und ihm dienen und ihm anhangen.', '5. Mose 13,5', 'Ihr scheint als Lichter in der Welt, dadurch dass ihr festhaltet am Wort des Lebens.', 'Philipper 2,15-16'),
('2025-06-13', 'Freitag', NULL, 'Sollte dem HERRN etwas unmöglich sein?', '1. Mose 18,14', 'Zuletzt, als die Elf zu Tisch saßen, offenbarte Jesus sich ihnen und schalt ihren Unglauben und ihres Herzens Härte, dass sie nicht geglaubt hatten denen, die ihn gesehen hatten als Auferstandenen.', 'Markus 16,14'),
('2025-06-14', 'Samstag', NULL, 'Die Frau sah, dass von dem Baum gut zu essen wäre und dass er eine Lust für die Augen wäre und verlockend, weil er klug machte.', '1. Mose 3,6', 'Die Welt vergeht, mit ihrem Begehren; wer aber den Willen Gottes tut, der bleibt in Ewigkeit.', '1. Johannes 2,17'),
('2025-06-15', 'Sonntag', 'Trinitatis (Dreieinigkeit)', 'Richtet recht, und ein jeder erweise seinem Bruder Güte und Barmherzigkeit!', 'Sacharja 7,9', 'Ein Diener des Herrn soll sich nicht streiten. Er soll zu allen freundlich sein, ein guter Lehrer, der stets geduldig bleibt. Diejenigen, die sich widersetzen, soll er mit Freundlichkeit zurechtweisen. Vielleicht gibt ihnen Gott die Möglichkeit, ihr Leben zu ändern.', '2. Timotheus 2,24-25'),
('2025-06-16', 'Montag', NULL, 'Eine linde Antwort stillt den Zorn; aber ein hartes Wort erregt Grimm.', 'Sprüche 15,1', 'Halte dich an das Vorbild der heilsamen Worte, die du von mir gehört hast, im Glauben und in der Liebe in Christus Jesus.', '2. Timotheus 1,13'),
('2025-06-17', 'Dienstag', NULL, 'Gott sprach zu Jakob: Ich bin Gott, der Gott deines Vaters; fürchte dich nicht. Ich will mit dir hinab nach Ägypten ziehen und will dich auch wieder heraufführen.', '1. Mose 46,3.4', 'Petrus sprach: Siehe, wir haben, was wir hatten, verlassen und sind dir nachgefolgt. Jesus aber sprach zu ihnen: Wahrlich, ich sage euch: Es ist niemand, der Haus oder Frau oder Brüder oder Eltern oder Kinder verlässt um des Reiches Gottes willen, der es nicht vielfach wieder empfange in dieser Zeit und in der kommenden Welt das ewige Leben.', 'Lukas 18,28-30'),
('2025-06-18', 'Mittwoch', NULL, 'Dein Wort ist meines Herzens Freude und Trost; denn ich bin ja nach deinem Namen genannt, HERR, Gott Zebaoth.', 'Jeremia 15,16', 'Philippus und der Eunuch stiegen ins Wasser, und Philippus taufte ihn. Als sie aus dem Wasser herausstiegen, wurde Philippus vom Geist des Herrn fortgenommen. Der Eunuch sah ihn nicht mehr. Aber er setzte seinen Weg voller Freude fort.', 'Apostelgeschichte 8,38-39'),
('2025-06-19', 'Donnerstag', NULL, 'Gott der HERR spricht: Ich will noch mehr sammeln zu der Schar derer, die versammelt sind.', 'Jesaja 56,8', 'Es wird gepredigt werden dies Evangelium vom Reich in der ganzen Welt zum Zeugnis für alle Völker, und dann wird das Ende kommen.', 'Matthäus 24,14'),
('2025-06-20', 'Freitag', NULL, 'Wende dich zu mir und sei mir gnädig; denn ich bin einsam und elend.', 'Psalm 25,16', 'Jesus, du Sohn Davids, erbarme dich meiner!', 'Markus 10,47'),
('2025-06-21', 'Samstag', NULL, 'Danket dem Herrn aller Herren, der allein große Wunder tut, denn seine Güte währet ewiglich.', 'Psalm 136,3.4', 'Das Volk neigte einmütig dem zu, was Philippus sagte, als sie ihm zuhörten und die Zeichen sahen, die er tat, und es kam große Freude auf in jener Stadt.', 'Apostelgeschichte 8,6.8'),
('2025-06-22', 'Sonntag', '1. Sonntag nach Trinitatis', 'Der HERR zog vor ihnen her, am Tage in einer Wolkensäule, um sie den rechten Weg zu führen, und bei Nacht in einer Feuersäule, um ihnen zu leuchten.', '2. Mose 13,21', 'Ihr sucht in den Schriften, denn ihr meint, ihr habt das ewige Leben darin; und sie sind’s, die von mir zeugen.', 'Johannes 5,39'),
('2025-06-23', 'Montag', NULL, 'Der HERR ist allen gütig und erbarmt sich aller seiner Werke.', 'Psalm 145,9', 'Auch die Schöpfung wird frei werden von der Knechtschaft der Vergänglichkeit zu der herrlichen Freiheit der Kinder Gottes.', 'Römer 8,21'),
('2025-06-24', 'Dienstag', 'Johannistag', 'Verlass dich auf den HERRN von ganzem Herzen, und verlass dich nicht auf deinen Verstand, sondern gedenke an ihn in allen deinen Wegen, so wird er dich recht führen.', 'Sprüche 3,5-6', 'Wahrlich, wahrlich, ich sage dir: Als du jünger warst, gürtetest du dich selbst und gingst, wo du hinwolltest; wenn du aber alt bist, wirst du deine Hände ausstrecken und ein anderer wird dich gürten und führen, wo du nicht hinwillst.', 'Johannes 21,18'),
('2025-06-25', 'Mittwoch', NULL, 'Jene, die fern sind, werden kommen und am Tempel des HERRN bauen.', 'Sacharja 6,15', 'Ja, alle Völker werden kommen und anbeten vor dir, denn deine Urteile sind offenbar geworden.', 'Offenbarung 15,4'),
('2025-06-26', 'Donnerstag', NULL, '/Der HERR sprach zu Mose:/ Versammle mir das Volk, dass ich sie meine Worte hören lasse und sie mich fürchten lernen alle Tage ihres Lebens auf Erden und ihre Kinder lehren.', '5. Mose 4,10', 'Du aber bleibe bei dem, was du gelernt hast und was dir anvertraut ist; du weißt ja, von wem du gelernt hast und dass du von Kind auf die Heilige Schrift kennst, die dich unterweisen kann zur Seligkeit durch den Glauben an Christus Jesus.', '2. Timotheus 3,14-15'),
('2025-06-27', 'Freitag', NULL, 'Der HERR tötet und macht lebendig, führt ins Totenreich und wieder herauf.', '1. Samuel 2,6', 'Leben wir, so leben wir dem Herrn; sterben wir, so sterben wir dem Herrn. Darum: wir leben oder sterben, so sind wir des Herrn.', 'Römer 14,8'),
('2025-06-28', 'Samstag', NULL, 'Er wird mit Gerechtigkeit richten die Armen und rechtes Urteil sprechen den Elenden im Lande.', 'Jesaja 11,4', 'Jesus sprach zu ihnen: Geht hin und sagt Johannes wieder, was ihr hört und seht: Blinde sehen und Lahme gehen, Aussätzige werden rein und Taube hören, Tote stehen auf und Armen wird das Evangelium gepredigt.', 'Matthäus 11,4-5'),
('2025-06-29', 'Sonntag', '2. Sonntag nach Trinitatis', 'Der HERR macht zunichte die Pläne der Völker.', 'Psalm 33,10', 'Lasst ab von diesen Leuten und lasst sie gehen! Denn wenn das, was hier geplant und ins Werk gesetzt wird, von Menschen stammen sollte, dann wird es sich zerschlagen. Wenn es aber von Gott kommt, dann werdet ihr sie nicht aufhalten können.', 'Apostelgeschichte 5,38-39'),
('2025-06-30', 'Montag', NULL, 'Warum gibt Gott dem Leidenden Licht und Leben denen, die verbittert sind, die sich sehnen nach dem Tod, doch er kommt nicht?', 'Hiob 3,20-21', 'Von der Geduld Hiobs habt ihr gehört und habt gesehen, zu welchem Ende es der Herr geführt hat; denn der Herr ist barmherzig und ein Erbarmer.', 'Jakobus 5,11'),
('2025-07-01', 'Dienstag', NULL, 'Wenn mir gleich Leib und Seele verschmachtet, so bist du doch, Gott, allezeit meines Herzens Trost und mein Teil.', 'Psalm 73,26', 'Das sagt der Heilige, der Wahrhaftige: Ich kenne deine Werke. Siehe, ich habe vor dir eine Tür aufgetan, die niemand zuschließen kann; denn du hast eine kleine Kraft und hast mein Wort bewahrt und hast meinen Namen nicht verleugnet.', 'Offenbarung 3,7.8'),
('2025-07-02', 'Mittwoch', NULL, 'Der HERR ist hoch und sieht auf den Niedrigen und kennt den Stolzen von ferne.', 'Psalm 138,6', 'Liebe Seele, du hast einen großen Vorrat für viele Jahre; habe nun Ruhe, iss, trink und habe guten Mut! Aber Gott sprach zu ihm: Du Narr! Diese Nacht wird man deine Seele von dir fordern. Und wem wird dann gehören, was du bereitet hast?', 'Lukas 12,19-20'),
('2025-07-03', 'Donnerstag', NULL, 'Des Menschen Herz erdenkt sich seinen Weg; aber der HERR allein lenkt seinen Schritt.', 'Sprüche 16,9', 'Alles, was ihr tut mit Worten oder mit Werken, das tut alles im Namen des Herrn Jesus und dankt Gott, dem Vater, durch ihn.', 'Kolosser 3,17'),
('2025-07-04', 'Freitag', NULL, 'Du bist mein Schutz und mein Schild; ich hoffe auf dein Wort.', 'Psalm 119,114', 'Das ist der Wille meines Vaters, dass, wer den Sohn sieht und glaubt an ihn, das ewige Leben habe; und ich werde ihn auferwecken am Jüngsten Tage.', 'Johannes 6,40'),
('2025-07-05', 'Samstag', NULL, 'Ach, HERR, sieh doch, wie bange ist mir. Mir dreht sich das Herz im Leibe um, weil ich so ungehorsam gewesen bin.', 'Klagelieder 1,20', 'Kommt, seht einen Menschen, der mir alles gesagt hat, was ich getan habe, ob er nicht der Christus sei!', 'Johannes 4,29'),
('2025-07-06', 'Sonntag', '3. Sonntag nach Trinitatis', 'Der Übeltäter lasse von seinen Gedanken und bekehre sich zum HERRN, denn bei ihm ist viel Vergebung.', 'Jesaja 55,7', 'Lasst euch versöhnen mit Gott!', '2. Korinther 5,20'),
('2025-07-07', 'Montag', NULL, 'Der HERR dachte an uns, als wir unterdrückt waren, denn seine Güte währet ewiglich.', 'Psalm 136,23', 'Betet für mich, dass mir das Wort gegeben werde, wenn ich meinen Mund auftue, freimütig das Geheimnis des Evangeliums zu verkündigen, dessen Bote ich bin in Ketten.', 'Epheser 6,19-20'),
('2025-07-08', 'Dienstag', NULL, 'Gott offenbart, was tief und verborgen ist.', 'Daniel 2,22', 'Gerechter Vater, die Welt kennt dich nicht; ich aber kenne dich, und diese haben erkannt, dass du mich gesandt hast.', 'Johannes 17,25'),
('2025-07-09', 'Mittwoch', NULL, 'Gleichwie ich über sie gewacht habe, auszureißen und einzureißen, so will ich über sie wachen, zu bauen und zu pflanzen, spricht der HERR.', 'Jeremia 31,28', 'Erschienen ist die Gnade Gottes, allen Menschen zum Heil. Sie erzieht uns dazu, der Gottlosigkeit und den Begierden der Welt abzuschwören und besonnen, gerecht und fromm zu leben in dieser Weltzeit.', 'Titus 2,11-12'),
('2025-07-10', 'Donnerstag', NULL, 'Haltet meine Gebote und tut danach; ich bin der HERR.', '3. Mose 22,31', 'Wer mich liebt, der wird mein Wort halten; und mein Vater wird ihn lieben, und wir werden zu ihm kommen und Wohnung bei ihm nehmen.', 'Johannes 14,23'),
('2025-07-11', 'Freitag', NULL, 'HERR, wenn ich an deine ewigen Ordnungen denke, so werde ich getröstet.', 'Psalm 119,52', 'Wir wissen: Wenn unser irdisches Haus, diese Hütte, abgebrochen wird, so haben wir einen Bau, von Gott erbaut, ein Haus, nicht mit Händen gemacht, das ewig ist im Himmel.', '2. Korinther 5,1'),
('2025-07-12', 'Samstag', NULL, 'So spricht der HERR: Ich habe dich erhört zur Zeit der Gnade und habe dir am Tage des Heils geholfen.', 'Jesaja 49,8', 'Jetzt ist sie da, die ersehnte Zeit, jetzt ist er da, der Tag der Rettung.', '2. Korinther 6,2'),
('2025-07-13', 'Sonntag', '4. Sonntag nach Trinitatis', 'HERR, von Herzen verlangt mich nach dir des Nachts, ja, mit meinem Geist suche ich dich am Morgen.', 'Jesaja 26,9', 'Am Morgen, noch vor Tage, stand Jesus auf und ging hinaus. Und er ging an eine einsame Stätte und betete dort.', 'Markus 1,35'),
('2025-07-14', 'Montag', NULL, 'Wo sind denn deine Götter, die du dir gemacht hast? Lass sie aufstehen; lass sehen, ob sie dir helfen können in deiner Not!', 'Jeremia 2,28', 'Meine Geliebten, flieht die Verehrung der nichtigen Götter!', '1. Korinther 10,14'),
('2025-07-15', 'Dienstag', NULL, '/Salomo sprach:/ Siehe, der Himmel und aller Himmel Himmel können dich nicht fassen – wie sollte es dann dies Haus tun, das ich gebaut habe?', '1. Könige 8,27', 'Die Stunde kommt, und sie ist jetzt da, in der die wahren Beter in Geist und Wahrheit zum Vater beten werden.', 'Johannes 4,23'),
('2025-07-16', 'Mittwoch', NULL, 'Wenn der HERR nicht die Stadt behütet, so wacht der Wächter umsonst.', 'Psalm 127,1', 'Betet für die Regierenden und für alle, die Gewalt haben, damit wir in Ruhe und Frieden leben können, in Ehrfurcht vor Gott und in Rechtschaffenheit.', '1. Timotheus 2,2'),
('2025-07-17', 'Donnerstag', NULL, 'Heile du mich, HERR, so werde ich heil; hilf du mir, so ist mir geholfen.', 'Jeremia 17,14', 'Damit ihr wisst, dass der Menschensohn Vollmacht hat auf Erden, Sünden zu vergeben – sprach Jesus zu dem Gelähmten: Ich sage dir, steh auf, nimm dein Bett und geh heim! Und sogleich stand er auf vor ihren Augen und nahm das Bett, auf dem er gelegen hatte, und ging heim und pries Gott.', 'Lukas 5,24-25'),
('2025-07-18', 'Freitag', NULL, 'Unsre Abtrünnigkeit steht uns vor Augen, und wir kennen unsre Sünden: abtrünnig sein und den HERRN verleugnen.', 'Jesaja 59,12-13', 'Wenn jemand sündigt, so haben wir einen Fürsprecher bei dem Vater, Jesus Christus, der gerecht ist.', '1. Johannes 2,1'),
('2025-07-19', 'Samstag', NULL, 'Meine Seele ist stille zu Gott, der mir hilft.', 'Psalm 62,2', 'Es ist gerecht bei Gott, dass er denen vergilt mit Bedrängnis, die euch bedrängen, euch aber, die ihr Bedrängnis leidet, Ruhe gibt zusammen mit uns, wenn der Herr Jesus offenbart wird vom Himmel her.', '2. Thessalonicher 1,6-7'),
('2025-07-20', 'Sonntag', '5. Sonntag nach Trinitatis', 'Du bist ja doch unter uns, HERR, und wir heißen nach deinem Namen; verlass uns nicht!', 'Jeremia 14,9', 'In Antiochia wurden die Jünger zuerst Christen genannt.', 'Apostelgeschichte 11,26'),
('2025-07-21', 'Montag', NULL, 'Friede, Friede denen in der Ferne und denen in der Nähe, spricht der HERR; ich will sie heilen.', 'Jesaja 57,19', 'Gott hat das Wort dem Volk Israel gesandt und Frieden verkündigt durch Jesus Christus, welcher ist Herr über alles.', 'Apostelgeschichte 10,36'),
('2025-07-22', 'Dienstag', NULL, 'Ehe ich gedemütigt wurde, irrte ich; nun aber halte ich dein Wort.', 'Psalm 119,67', 'Jesus spricht zu Simon Petrus: Simon, Sohn des Johannes, hast du mich lieb? Er spricht zu ihm: Ja, Herr, du weißt, dass ich dich lieb habe. Spricht Jesus zu ihm: Weide meine Schafe!', 'Johannes 21,16'),
('2025-07-23', 'Mittwoch', NULL, 'Der HERR führte mich hinaus ins Weite, er befreite mich, denn er hat Gefallen an mir.', 'Psalm 18,20', 'Durch Gottes Gnade bin ich, was ich bin. Und seine Gnade an mir ist nicht vergeblich gewesen.', '1. Korinther 15,10'),
('2025-07-24', 'Donnerstag', NULL, 'Höre mein Gebet, HERR, und vernimm mein Schreien, schweige nicht zu meinen Tränen.', 'Psalm 39,13', 'Eine Sünderin trat von hinten zu Jesu Füßen, weinte und fing an, seine Füße mit Tränen zu netzen und mit den Haaren ihres Hauptes zu trocknen, und küsste seine Füße und salbte sie mit dem Salböl. Jesus aber sprach zu der Frau: Dein Glaube hat dir geholfen; geh hin in Frieden!', 'Lukas 7,38.50'),
('2025-07-25', 'Freitag', NULL, 'Gott, wir haben mit unsern Ohren gehört, unsre Väter haben’s uns erzählt, was du getan hast zu ihren Zeiten, vor alters.', 'Psalm 44,2', 'Es wird eine Zeit kommen, da sie die heilsame Lehre nicht ertragen werden; sondern nach ihrem eigenen Begehren werden sie sich selbst Lehrer aufladen, nach denen ihnen die Ohren jucken.', '2. Timotheus 4,3'),
('2025-07-26', 'Samstag', NULL, 'Ich will euch heimsuchen, spricht der HERR, nach der Frucht eures Tuns.', 'Jeremia 21,14', 'Sein Herr sprach zu ihm: Recht so, du guter und treuer Knecht, du bist über wenigem treu gewesen, ich will dich über viel setzen; geh hinein zu deines Herrn Freude!', 'Matthäus 25,21'),
('2025-07-27', 'Sonntag', '6. Sonntag nach Trinitatis', 'Der HERR wird seinem Volk Kraft geben.', 'Psalm 29,11', 'Gott hat uns nicht gegeben den Geist der Furcht, sondern der Kraft und der Liebe und der Besonnenheit.', '2. Timotheus 1,7'),
('2025-07-28', 'Montag', NULL, 'Du sollst den Namen des HERRN, deines Gottes, nicht missbrauchen; denn der HERR wird den nicht ungestraft lassen, der seinen Namen missbraucht.', '2. Mose 20,7', 'Unser Vater im Himmel! Dein Name werde geheiligt.', 'Matthäus 6,9'),
('2025-07-29', 'Dienstag', NULL, 'HERR, erhebe dich in deiner Kraft, so wollen wir singen und loben deine Macht.', 'Psalm 21,14', 'Als Jesus schon nahe am Abhang des Ölbergs war, fing die ganze Menge der Jünger an, mit Freuden Gott zu loben mit lauter Stimme über alle Taten, die sie gesehen hatten.', 'Lukas 19,37'),
('2025-07-30', 'Mittwoch', NULL, 'Behalte meine Gebote, so wirst du leben, und hüte meine Weisung wie deinen Augapfel.', 'Sprüche 7,2', 'Da sagte der junge Mann zu Jesus: Das alles habe ich befolgt. Was fehlt mir noch?', 'Matthäus 19,20'),
('2025-07-31', 'Donnerstag', NULL, 'So hoch der Himmel über der Erde ist, lässt er seine Gnade walten über denen, die ihn fürchten.', 'Psalm 103,11', 'Wo aber die Sünde ihr volles Maß erreicht hatte, da wuchs die Gnade über alles Maß hinaus. Wie die Sünde ihre Macht ausübte, indem sie den Tod brachte, so wird die Gnade ihre Macht ausüben, indem sie uns vor Gott bestehen lässt und zum ewigen Leben führt. Das verdanken wir Jesus Christus, unserem Herrn.', 'Römer 5,20-21'),
('2025-08-01', 'Freitag', NULL, 'Bist du es nicht, HERR, unser Gott, auf den wir hoffen?', 'Jeremia 14,22', 'Gott hat uns ewiges Leben gegeben, und wir erhalten dieses Leben durch seinen Sohn.', '1. Johannes 5,11'),
('2025-08-02', 'Samstag', NULL, 'Denke keiner gegen seinen Bruder etwas Arges in seinem Herzen!', 'Sacharja 7,10', 'Euch allen sage ich: Haltet in derselben Gesinnung zusammen und habt Mitgefühl füreinander! Liebt euch gegenseitig als Brüder und Schwestern! Seid gütig und zuvorkommend zueinander!', '1. Petrus 3,8'),
('2025-08-03', 'Sonntag', '7. Sonntag nach Trinitatis', '/Der HERR spricht:/ Ich will dich unterweisen und dir den Weg zeigen, den du gehen sollst.', 'Psalm 32,8', 'Folge du mir nach!', 'Johannes 21,22'),
('2025-08-04', 'Montag', NULL, 'Der HERR ist ein Gott des Rechts. Wohl allen, die auf ihn harren!', 'Jesaja 30,18', 'Wenn es jemandem unter euch an Weisheit mangelt, so bitte er Gott, der jedermann gern und ohne Vorwurf gibt; so wird sie ihm gegeben werden.', 'Jakobus 1,5'),
('2025-08-05', 'Dienstag', NULL, 'Der HERR sprach zu Mose: Du sollst alles reden, was ich dir gebieten werde.', '2. Mose 7,1.2', 'Verkündige das Wort, tritt dafür ein, zur Zeit oder Unzeit, widerlege, tadle, bitte, in aller Geduld, wo die Lehre es gebietet!', '2. Timotheus 4,2'),
('2025-08-06', 'Mittwoch', NULL, 'Ich will dem HERRN singen, denn er ist hoch erhaben.', '2. Mose 15,1', 'Ich will beten mit dem Geist und will auch beten mit dem Verstand; ich will Psalmen singen mit dem Geist und will auch Psalmen singen mit dem Verstand.', '1. Korinther 14,15'),
('2025-08-07', 'Donnerstag', NULL, 'Ihr erwartet wohl viel, aber siehe, es wird wenig; und wenn ihr’s schon heimbringt, so blase ich’s weg. Warum das?, spricht der HERR Zebaoth. Weil mein Haus so wüst dasteht; ihr aber eilt, ein jeder für sein Haus zu sorgen.', 'Haggai 1,9', 'Weh euch Pharisäern! Denn ihr gebt den Zehnten von Minze und Raute und allem Kraut und geht vorbei am Recht und an der Liebe Gottes. Doch dies sollte man tun und jenes nicht lassen.', 'Lukas 11,42'),
('2025-08-08', 'Freitag', NULL, 'Ich habe mir vorgenommen: Ich will mich hüten, dass ich nicht sündige mit meiner Zunge.', 'Psalm 39,2', 'Aus #einem# Munde kommt Loben und Fluchen. Das soll nicht so sein, meine Brüder und Schwestern.', 'Jakobus 3,10'),
('2025-08-09', 'Samstag', NULL, 'Tut von euch die fremden Götter, die unter euch sind, und neigt euer Herz zu dem HERRN.', 'Josua 24,23', 'Ich habe nichts verschwiegen, was für euch wichtig ist. Ich habe Juden und Griechen beschworen, zu Gott umzukehren und an Jesus, unseren Herrn, zu glauben.', 'Apostelgeschichte 20,20.21'),
('2025-08-10', 'Sonntag', '8. Sonntag nach Trinitatis', 'Der HERR segne dich und behüte dich.', '4. Mose 6,24', 'Seid gewiss: Ich bin bei euch alle Tage bis an der Welt Ende.', 'Matthäus 28,20'),
('2025-08-11', 'Montag', NULL, 'Wer des HERRN Namen anrufen wird, der soll errettet werden.', 'Joel 3,5', 'Jesus sprach: Seht zu, lasst euch nicht verführen. Denn viele werden kommen unter meinem Namen und sagen: Ich bin’s, und: Die Zeit ist herbeigekommen. – Lauft ihnen nicht nach!', 'Lukas 21,8'),
('2025-08-12', 'Dienstag', NULL, 'Der HERR lebt! Gelobt sei mein Fels!', 'Psalm 18,47', 'Jesus sprach zu ihnen: So steht’s geschrieben, dass der Christus leiden wird und auferstehen von den Toten am dritten Tage.', 'Lukas 24,46'),
('2025-08-13', 'Mittwoch', NULL, 'Gottes Wahrheit ist Schirm und Schild.', 'Psalm 91,4', 'Wer die Wahrheit tut, der kommt zu dem Licht, damit offenbar wird, dass seine Werke in Gott getan sind.', 'Johannes 3,21'),
('2025-08-14', 'Donnerstag', NULL, 'Der HERR sprach: Ich habe vergeben, wie du es erbeten hast.', '4. Mose 14,20', 'Wenn ihr steht und betet, so vergebt, wenn ihr etwas gegen jemanden habt, damit auch euer Vater im Himmel euch vergebe eure Übertretungen.', 'Markus 11,25'),
('2025-08-15', 'Freitag', NULL, 'Ich will dich loben mein Leben lang und meine Hände in deinem Namen aufheben.', 'Psalm 63,5', 'Das ganze Volk sah den Geheilten umhergehen und Gott loben.', 'Apostelgeschichte 3,9'),
('2025-08-16', 'Samstag', NULL, 'Ist’s nicht so: Wenn du fromm bist, so kannst du frei den Blick erheben. Bist du aber nicht fromm, so lauert die Sünde vor der Tür, und nach dir hat sie Verlangen; du aber herrsche über sie.', '1. Mose 4,7', 'Als Judas, der Jesus verraten hatte, sah, dass er zum Tode verurteilt war, reute es ihn, und er brachte die dreißig Silberlinge den Hohenpriestern und Ältesten zurück und sprach: Ich habe gesündigt, unschuldiges Blut habe ich verraten.', 'Matthäus 27,3-4'),
('2025-08-17', 'Sonntag', '9. Sonntag nach Trinitatis', 'Unser Leben währet siebzig Jahre, und wenn’s hoch kommt, so sind’s achtzig Jahre, und was daran köstlich scheint, ist doch nur vergebliche Mühe; denn es fähret schnell dahin, als flögen wir davon.', 'Psalm 90,10', 'Christus möchte ich erkennen und die Kraft seiner Auferstehung.', 'Philipper 3,10'),
('2025-08-18', 'Montag', NULL, 'Machet kund unter den Völkern sein Tun, verkündiget, wie sein Name so hoch ist!', 'Jesaja 12,4', 'Geht nun hin und macht alle Völker zu Jüngern: Tauft sie auf den Namen des Vaters und des Sohnes und des heiligen Geistes, und lehrt sie alles halten, was ich euch geboten habe.', 'Matthäus 28,19-20'),
('2025-08-19', 'Dienstag', NULL, 'Der Himmel wird wie ein Rauch vergehen und die Erde wie ein Kleid zerfallen, und die darauf wohnen, werden wie Mücken dahinsterben. Aber mein Heil bleibt ewiglich.', 'Jesaja 51,6', 'Wenn aber kommen wird das Vollkommene, so wird das Stückwerk aufhören.', '1. Korinther 13,10'),
('2025-08-20', 'Mittwoch', NULL, 'Nur Hauch sind die Menschen, Trug die Sterblichen. Auf der Waage schnellen sie empor, allesamt leichter als Hauch.', 'Psalm 62,10', 'Sammelt euch Schätze im Himmel, wo weder Motten noch Rost sie fressen und wo Diebe nicht einbrechen und stehlen. Denn wo dein Schatz ist, da ist auch dein Herz.', 'Matthäus 6,20-21'),
('2025-08-21', 'Donnerstag', NULL, '/Gott spricht:/ Im Schweiß deines Angesichts wirst du dein Brot essen, bis du zum Erdboden zurückkehrst, denn von ihm bist du genommen. Denn Staub bist du, und zum Staub kehrst du zurück.', '1. Mose 3,19', 'Wer auf den Boden seiner selbstsüchtigen Natur sät, wird von seiner Selbstsucht das Verderben ernten. Aber wer auf den Boden von Gottes Geist sät, wird von diesem Geist das ewige Leben ernten.', 'Galater 6,8'),
('2025-08-22', 'Freitag', NULL, 'Nehmt euch in acht bei eurem Tun, denn beim HERRN, unserem Gott, gibt es keine Ungerechtigkeit und kein Ansehen der Person und keine Bestechlichkeit.', '2. Chronik 19,7', 'Prüft, was dem Herrn wohlgefällig ist.', 'Epheser 5,10'),
('2025-08-23', 'Samstag', NULL, 'Mir hast du Arbeit gemacht mit deinen Sünden und hast mir Mühe gemacht mit deinen Missetaten. Ich, ich tilge deine Übertretungen um meinetwillen und gedenke deiner Sünden nicht.', 'Jesaja 43,24-25', 'Jesus antwortete ihnen: Mein Vater wirkt bis auf diesen Tag, und ich wirke auch.', 'Johannes 5,17'),
('2025-08-24', 'Sonntag', '10. Sonntag nach Trinitatis (Israelsonntag)', 'Wir, dein Volk, die Schafe deiner Weide, danken dir ewiglich und verkünden deinen Ruhm für und für.', 'Psalm 79,13', 'Ich bin der gute Hirte und kenne die Meinen und die Meinen kennen mich.', 'Johannes 10,14'),
('2025-08-25', 'Montag', NULL, 'Wenn du auf die Stimme des HERRN, deines Gottes, hörst: Gesegnet bist du in der Stadt, und gesegnet bist du auf dem Feld.', '5. Mose 28,2-3', 'Ihr bittet und empfangt’s nicht, weil ihr in übler Absicht bittet, nämlich damit ihr’s für eure Gelüste vergeuden könnt.', 'Jakobus 4,3'),
('2025-08-26', 'Dienstag', NULL, 'Der HERR behütet dich; der HERR ist dein Schatten über deiner rechten Hand, dass dich des Tages die Sonne nicht steche noch der Mond des Nachts.', 'Psalm 121,5-6', 'Gnade sei mit euch und Friede von Gott, unserm Vater, und dem Herrn Jesus Christus!', 'Philemon 1,3'),
('2025-08-27', 'Mittwoch', NULL, 'Wen der HERR liebt, den weist er zurecht, und hat doch Wohlgefallen an ihm wie ein Vater am Sohn.', 'Sprüche 3,12', 'Feiern muss man jetzt und sich freuen, denn dieser dein Bruder war tot und ist lebendig geworden, war verloren und ist gefunden worden.', 'Lukas 15,32'),
('2025-08-28', 'Donnerstag', NULL, 'Du wirst sein eine schöne Krone in der Hand des HERRN und ein königlicher Reif in der Hand deines Gottes.', 'Jesaja 62,3', 'Wenn du eingeladen bist, so geh hin und setz dich untenan, damit, wenn der kommt, der dich eingeladen hat, er zu dir sagt: Freund, rücke hinauf! Dann wirst du Ehre haben vor allen, die mit dir zu Tisch sitzen.', 'Lukas 14,10'),
('2025-08-29', 'Freitag', NULL, 'HERR Zebaoth, du bist allein Gott über alle Königreiche auf Erden, du hast Himmel und Erde gemacht.', 'Jesaja 37,16', 'Alles ist durch ihn geworden, und ohne ihn ist auch nicht eines geworden, das geworden ist.', 'Johannes 1,3'),
('2025-08-30', 'Samstag', NULL, 'Herr, du lässt mich genesen und am Leben bleiben.', 'Jesaja 38,16', 'Es traf sich aber, dass der Vater des Publius mit Fieber und Durchfall darniederlag. Da ging Paulus zu ihm hinein und betete, legte ihm die Hände auf und machte ihn gesund.', 'Apostelgeschichte 28,8'),
('2025-08-31', 'Sonntag', '11. Sonntag nach Trinitatis', 'Wohl allen, die auf ihn trauen!', 'Psalm 2,12', 'Durch Christus Jesus haben wir Freimut und Zugang in aller Zuversicht durch den Glauben an ihn.', 'Epheser 3,12'),
('2025-09-01', 'Montag', NULL, 'Haltet dem HERRN, eurem Gott, die Treue, so wie ihr es bisher getan habt.', 'Josua 23,8', 'Wir wollen die Versammlung der Gemeinde nicht verlassen, wie es bei einigen üblich geworden ist, sondern einander mit Zuspruch beistehen, und dies umso mehr, als ihr den Tag nahen seht.', 'Hebräer 10,25'),
('2025-09-02', 'Dienstag', NULL, 'Wer bin ich, Herr HERR, und was ist mein Haus, dass du mich bis hierher gebracht hast?', '2. Samuel 7,18', 'Als Lydia aber mit ihrem Hause getauft war, bat sie uns und sprach: Wenn ihr anerkennt, dass ich an den Herrn glaube, so kommt in mein Haus und bleibt da.', 'Apostelgeschichte 16,15'),
('2025-09-03', 'Mittwoch', NULL, 'Ich will euch ein neues Herz und einen neuen Geist in euch geben.', 'Hesekiel 36,26', 'Liebt eure Feinde und bittet für die, die euch verfolgen, auf dass ihr Kinder seid eures Vaters im Himmel.', 'Matthäus 5,44-45'),
('2025-09-04', 'Donnerstag', NULL, 'Von Gott werde dir geholfen, und von dem Allmächtigen seist du gesegnet.', '1. Mose 49,25', 'Das Evangelium ist eine Kraft Gottes, die selig macht alle, die glauben.', 'Römer 1,16'),
('2025-09-05', 'Freitag', NULL, 'Nimm ja nicht von meinem Munde das Wort der Wahrheit.', 'Psalm 119,43', 'Sie haben Gottes Wahrheit in Lüge verkehrt und das Geschöpf verehrt und ihm gedient statt dem Schöpfer, der gelobt ist in Ewigkeit.', 'Römer 1,25'),
('2025-09-06', 'Samstag', NULL, '/Gott spricht:/ Ich will für Israel wie der Tau sein, dass es blüht wie eine Lilie.', 'Hosea 14,6', 'Darum sollt ihr nicht sorgen und sagen: Was werden wir essen? Was werden wir trinken? Womit werden wir uns kleiden? Denn euer himmlischer Vater weiß, dass ihr all dessen bedürft.', 'Matthäus 6,31.32'),
('2025-09-07', 'Sonntag', '12. Sonntag nach Trinitatis', 'Ich will dem HERRN sehr danken mit meinem Munde und ihn rühmen in der Menge.', 'Psalm 109,30', 'Jesus legte die Hände auf die verkrümmte Frau; und sogleich richtete sie sich auf und pries Gott.', 'Lukas 13,13'),
('2025-09-08', 'Montag', NULL, 'Gott sende seine Güte und Treue.', 'Psalm 57,4', 'Die Frucht des Geistes ist Liebe, Freude, Frieden, Geduld, Güte, Rechtschaffenheit, Treue, Sanftmut, Selbstbeherrschung.', 'Galater 5,22-23'),
('2025-09-09', 'Dienstag', NULL, 'Es hat dem HERRN gefallen, euch zu seinem Volk zu machen.', '1. Samuel 12,22', 'Die Apostel priesen Gott und sprachen: Nun hat Gott also auch den anderen Völkern die Umkehr zum Leben gewährt.', 'Apostelgeschichte 11,18'),
('2025-09-10', 'Mittwoch', NULL, 'Unser Gott, du großer Gott, mächtig und schrecklich, der du Bund und Treue hältst, achte nicht gering all das Elend, das uns getroffen hat.', 'Nehemia 9,32', 'Ich bin gewiss, dass weder Tod noch Leben, weder Engel noch Mächte noch Gewalten, weder Gegenwärtiges noch Zukünftiges, weder Hohes noch Tiefes noch irgendeine andere Kreatur uns scheiden kann von der Liebe Gottes, die in Christus Jesus ist, unserm Herrn.', 'Römer 8,38-39'),
('2025-09-11', 'Donnerstag', NULL, 'Ich bin der HERR. Was ich rede, das soll geschehen und sich nicht lange hinausziehen.', 'Hesekiel 12,25', 'Da nun die Schrift voraussah, dass Gott die Völker aus Glauben gerecht machen würde, hat sie dem Abraham das Evangelium im Voraus verkündigt: In dir werden alle Völker gesegnet werden.', 'Galater 3,8'),
('2025-09-12', 'Freitag', NULL, 'Fürchte dich nicht vor plötzlichem Schrecken; denn der HERR ist deine Zuversicht.', 'Sprüche 3,25.26', 'Als die Jünger Jesus auf dem See gehen sahen, erschraken sie, weil sie meinten, es sei ein Gespenst, und sie schrien vor Angst. Sogleich aber redete Jesus mit ihnen: Seid getrost, ich bin es. Fürchtet euch nicht!', 'Matthäus 14,26-27'),
('2025-09-13', 'Samstag', NULL, 'Auf Gott hoffe ich und fürchte mich nicht; was können mir Menschen tun?', 'Psalm 56,12', 'So hatte nun die Gemeinde Frieden in ganz Judäa und Galiläa und Samarien und baute sich auf und lebte in der Furcht des Herrn und mehrte sich unter dem Beistand des Heiligen Geistes.', 'Apostelgeschichte 9,31'),
('2025-09-14', 'Sonntag', '13. Sonntag nach Trinitatis', 'Ich will mich freuen des HERRN und fröhlich sein in Gott, meinem Heil.', 'Habakuk 3,18', 'Als der Sohn noch weit entfernt war, sah ihn sein Vater und es jammerte ihn, und er lief und fiel ihm um den Hals und küsste ihn.', 'Lukas 15,20'),
('2025-09-15', 'Montag', NULL, '/Der HERR sprach zu Mose:/ Ich will ihnen einen Propheten, wie du bist, erwecken aus ihren Brüdern und meine Worte in seinen Mund geben.', '5. Mose 18,18', 'Und viele, die zuhörten, verwunderten sich und sprachen: Ist der nicht der Zimmermann?', 'Markus 6,2.3'),
('2025-09-16', 'Dienstag', NULL, 'Die Himmel erzählen die Ehre Gottes.', 'Psalm 19,2', 'Preist Gott mit eurem Leibe.', '1. Korinther 6,20'),
('2025-09-17', 'Mittwoch', NULL, '/Jeremia sprach:/ Ich dachte: Ich will seiner nicht mehr gedenken und nicht mehr in seinem Namen predigen. Aber es ward in meinem Herzen wie ein brennendes Feuer.', 'Jeremia 20,9', 'Betet zugleich auch für uns, auf dass Gott uns eine Tür für das Wort auftue und wir vom Geheimnis Christi reden können, um dessentwillen ich auch in Fesseln bin, auf dass ich es so offenbar mache, wie ich es soll.', 'Kolosser 4,3-4'),
('2025-09-18', 'Donnerstag', NULL, '/Der HERR spricht:/ Warum zählt ihr Geld dar für das, was kein Brot ist, und euren sauren Verdienst für das, was nicht satt macht? Hört doch auf mich, so werdet ihr Gutes essen.', 'Jesaja 55,2', 'Diese Witwe hat von ihrer Armut ihre ganze Habe eingelegt, alles, was sie zum Leben hatte.', 'Markus 12,44'),
('2025-09-19', 'Freitag', NULL, 'Was vom Hause Juda errettet und übrig geblieben ist, wird von Neuem nach unten Wurzeln schlagen und oben Frucht tragen.', '2. Könige 19,30', 'Bleibt in mir und ich in euch. Wie die Rebe keine Frucht bringen kann aus sich selbst, wenn sie nicht am Weinstock bleibt, so auch ihr nicht, wenn ihr nicht an mir bleibt.', 'Johannes 15,4'),
('2025-09-20', 'Samstag', NULL, 'Lehre mich tun nach deinem Wohlgefallen, denn du bist mein Gott; dein guter Geist führe mich auf ebner Bahn.', 'Psalm 143,10', 'Alle, die sich vom Geist Gottes führen lassen, die sind Gottes Söhne und Töchter.', 'Römer 8,14'),
('2025-09-21', 'Sonntag', '14. Sonntag nach Trinitatis', '/Rut sprach:/ Bedränge mich nicht, dass ich dich verlassen und von dir umkehren sollte. Wo du hingehst, da will ich auch hingehen; wo du bleibst, da bleibe ich auch. Dein Volk ist mein Volk, und dein Gott ist mein Gott.', 'Rut 1,16', 'Boas zeugte Obed; die Mutter war Rut. Obed zeugte Isai. Isai zeugte den König David. Jakob zeugte Josef, den Mann von Maria. Sie wurde die Mutter von Jesus, der Christus genannt wird.', 'Matthäus 1,5.6.16'),
('2025-09-22', 'Montag', NULL, 'Was der HERR tut, das ist herrlich und prächtig, und seine Gerechtigkeit bleibt ewiglich.', 'Psalm 111,3', 'Ich habe nicht meine eigene Gerechtigkeit, die aus dem Gesetz kommt, sondern jene Gerechtigkeit durch den Glauben an Christus, die aus Gott kommt aufgrund des Glaubens.', 'Philipper 3,9'),
('2025-09-23', 'Dienstag', NULL, 'Ich will euch von all eurer Unreinheit erlösen.', 'Hesekiel 36,29', 'Gott hat uns nicht zur Unsittlichkeit berufen, sondern zu einem Leben in Heiligkeit.', '1. Thessalonicher 4,7'),
('2025-09-24', 'Mittwoch', NULL, 'Hilf du uns, Gott, unser Helfer, um deines Namens Ehre willen! Errette uns und vergib uns unsre Sünden um deines Namens willen!', 'Psalm 79,9', 'In seinem Namen wird allen Völkern Umkehr verkündigt werden zur Vergebung der Sünden.', 'Lukas 24,47'),
('2025-09-25', 'Donnerstag', NULL, 'Siehe: Der die Berge gemacht und den Wind geschaffen hat, der dem Menschen sagt, was er im Sinne hat – er heißt »HERR, Gott Zebaoth«.', 'Amos 4,13', 'Halleluja! König geworden ist der Herr, unser Gott, der Herrscher über das All.', 'Offenbarung 19,6'),
('2025-09-26', 'Freitag', NULL, 'Viele sagen von mir: Er hat keine Hilfe bei Gott. Aber du, HERR, bist der Schild für mich, du bist meine Ehre und hebst mein Haupt empor.', 'Psalm 3,3-4', 'Einer aber unter den zehn aussätzigen Männern, als er sah, dass er gesund geworden war, kehrte er um und pries Gott mit lauter Stimme.', 'Lukas 17,15'),
('2025-09-27', 'Samstag', NULL, 'Fürchtet euch nur nicht und stärkt eure Hände!', 'Sacharja 8,13', 'In der Welt habt ihr Angst; aber seid getrost, ich habe die Welt überwunden.', 'Johannes 16,33'),
('2025-09-28', 'Sonntag', '15. Sonntag nach Trinitatis', 'Der HERR behütet die Fremdlinge und erhält Waisen und Witwen.', 'Psalm 146,9', 'Vergesst die Gastfreundschaft nicht. Denn auf diese Weise haben manche, ohne es zu wissen, Engel als Gäste aufgenommen.', 'Hebräer 13,2'),
('2025-09-29', 'Montag', 'Michaelistag', 'Ich bin dein, hilf mir.', 'Psalm 119,94', 'Plötzlich stand da der Engel des Herrn, und die ganze Zelle war von strahlendem Licht erfüllt. Der Engel weckte Petrus durch einen Stoß in die Seite und sagte: Schnell, steh auf! Da fielen Petrus die Ketten von den Händen.', 'Apostelgeschichte 12,7'),
('2025-09-30', 'Dienstag', NULL, 'Mache dich auf, hilf uns und erlöse uns um deiner Güte willen!', 'Psalm 44,27', 'Der Gott aller Gnade aber, der euch berufen hat zu seiner ewigen Herrlichkeit in Christus, der wird euch, die ihr eine kleine Zeit leidet, aufrichten, stärken, kräftigen, gründen.', '1. Petrus 5,10'),
('2025-10-01', 'Mittwoch', NULL, 'Der HERR erweckte den Geist des Volkes, dass sie kamen und arbeiteten am Hause des HERRN Zebaoth, ihres Gottes.', 'Haggai 1,14', 'Ich kenne deine Werke und deine Mühsal und deine Geduld.', 'Offenbarung 2,2'),
('2025-10-02', 'Donnerstag', NULL, 'Kommt her und sehet an die Werke Gottes, der so wunderbar ist in seinem Tun an den Menschenkindern.', 'Psalm 66,5', 'Jesus sprach zu dem Hauptmann: Geh hin; dir geschehe, wie du geglaubt hast. Und sein Knecht wurde gesund zu derselben Stunde.', 'Matthäus 8,13'),
('2025-10-03', 'Freitag', NULL, 'Wer glaubt dem, was uns verkündet wurde, und an wem ist der Arm des HERRN offenbart?', 'Jesaja 53,1', 'Maria Magdalena geht und verkündigt den Jüngern: Ich habe den Herrn gesehen, und was er zu ihr gesagt habe.', 'Johannes 20,18'),
('2025-10-04', 'Samstag', NULL, '/Salomo betete:/ Du hast deinem Knecht, meinem Vater David, gehalten, was du ihm zugesagt hast. Mit deinem Mund hast du es geredet, und mit deiner Hand hast du es erfüllt, wie es offenbar ist an diesem Tage.', '1. Könige 8,24', 'Treu ist er, der euch ruft.', '1. Thessalonicher 5,24'),
('2025-10-05', 'Sonntag', 'Erntedankfest', 'Es sei Gutes oder Schlechtes – auf die Stimme des HERRN, unseres Gottes, werden wir hören.', 'Jeremia 42,6', 'Jesus aber antwortete den Jüngern: Gebt ihr ihnen zu essen!', 'Markus 6,37'),
('2025-10-06', 'Montag', NULL, 'Ich glaube aber doch, dass ich sehen werde die Güte des HERRN im Lande der Lebendigen.', 'Psalm 27,13', 'Das Gebet des Glaubens wird dem Kranken helfen, und der Herr wird ihn aufrichten; und wenn er Sünden getan hat, wird ihm vergeben werden.', 'Jakobus 5,15'),
('2025-10-07', 'Dienstag', NULL, 'Ich erkenne, HERR, dass du alles vermagst, und nichts, das du dir vorgenommen, ist dir zu schwer.', 'Hiob 42,2', 'Bei Gott ist kein Ding unmöglich.', 'Lukas 1,37'),
('2025-10-08', 'Mittwoch', NULL, 'Das Verlangen der Elenden hörst du, HERR; du machst ihr Herz gewiss.', 'Psalm 10,17', 'Und als Jesus an die Stelle kam, sah er auf und sprach zu ihm: Zachäus, steig eilend herunter; denn ich muss heute in deinem Haus einkehren. Und er stieg eilend herunter und nahm ihn auf mit Freuden.', 'Lukas 19,5-6'),
('2025-10-09', 'Donnerstag', NULL, '/Der HERR sprach zu Abram:/ In dir sollen gesegnet werden alle Geschlechter auf Erden.', '1. Mose 12,1.3', 'Alle, die aus Glauben leben, werden zusammen mit dem glaubenden Abraham Segen empfangen.', 'Galater 3,9'),
('2025-10-10', 'Freitag', NULL, 'Ich hatte dich gepflanzt als einen edlen Weinstock, ein ganz echtes Gewächs. Wie bist du mir denn geworden zu einem schlechten, wilden Weinstock?', 'Jeremia 2,21', 'Gott hat das Volk, das er von Anfang an erwählt hatte, nicht verstoßen.', 'Römer 11,2'),
('2025-10-11', 'Samstag', NULL, 'HERR, unser Herrscher, wie herrlich ist dein Name in allen Landen.', 'Psalm 8,2', 'Ihr werdet meine Zeugen sein in Jerusalem und in ganz Judäa und Samarien und bis an das Ende der Erde.', 'Apostelgeschichte 1,8'),
('2025-10-12', 'Sonntag', '17. Sonntag nach Trinitatis', 'Seine Macht ist ewig und vergeht nicht, und sein Reich hat kein Ende.', 'Daniel 7,14', 'Jesus Christus ist derselbe gestern, heute und in Ewigkeit.', 'Hebräer 13,8'),
('2025-10-13', 'Montag', NULL, 'So spricht der HERR: Dein Schaden ist verzweifelt böse, und deine Wunden sind unheilbar. Doch ich will dich wieder gesund machen und deine Wunden heilen.', 'Jeremia 30,12.17', 'Ihr wisst: Jesus Christus ist gekommen, um die Sünden wegzunehmen. Er selbst ist ohne Sünde.', '1. Johannes 3,5-6'),
('2025-10-14', 'Dienstag', NULL, 'Du hast meine Seele vom Tode errettet, mein Auge von den Tränen, meinen Fuß vom Gleiten.', 'Psalm 116,8', 'Eine Frau hatte den Blutfluss seit zwölf Jahren; die hatte alles, was sie zum Leben hatte, für die Ärzte aufgewandt und konnte von niemandem geheilt werden. Die trat von hinten heran und berührte den Saum seines Gewandes; und sogleich hörte ihr Blutfluss auf.', 'Lukas 8,43-44'),
('2025-10-15', 'Mittwoch', NULL, 'Ihr sollt in Freuden ausziehen und im Frieden geleitet werden.', 'Jesaja 55,12', 'Die 72 Jünger kehrten zurück und berichteten voller Freude: Herr, sogar die Dämonen gehorchen uns, wenn wir uns auf deinen Namen berufen. Jesus sagte zu ihnen: Ihr sollt euch nicht darüber freuen, dass euch die Geister gehorchen. Freut euch vielmehr darüber, dass eure Namen im Himmel aufgeschrieben sind.', 'Lukas 10,17.18.20'),
('2025-10-16', 'Donnerstag', NULL, 'Fürchte dich nicht, denn ich bin mit dir und will dich segnen.', '1. Mose 26,24', 'Gelobt sei Gott, der Vater unseres Herrn Jesus Christus, der uns gesegnet hat mit allem geistlichen Segen im Himmel durch Christus.', 'Epheser 1,3'),
('2025-10-17', 'Freitag', NULL, 'Ich lasse dich nicht, du segnest mich denn.', '1. Mose 32,27', 'Da wurden Kinder zu Jesus gebracht, dass er die Hände auf sie legte und betete.', 'Matthäus 19,13'),
('2025-10-18', 'Samstag', NULL, 'Bei dir, Herr, unser Gott, ist Barmherzigkeit und Vergebung.', 'Daniel 9,9', 'In Jesus Christus haben wir die Erlösung durch sein Blut, die Vergebung der Sünden, nach dem Reichtum seiner Gnade,', 'Epheser 1,7'),
('2025-10-19', 'Sonntag', '18. Sonntag nach Trinitatis', 'Gott, schweige doch nicht! Gott, bleib nicht so still und ruhig! Denn siehe, deine Feinde toben, und die dich hassen, erheben das Haupt.', 'Psalm 83,2-3', 'Herr, sollen wir mit dem Schwert dreinschlagen?', 'Lukas 22,49'),
('2025-10-20', 'Montag', NULL, 'Seine Herrschaft wird sein von einem Meer bis zum andern und bis an die Enden der Erde.', 'Sacharja 9,10', 'Wiederum führte der Teufel Jesus mit sich auf einen sehr hohen Berg und zeigte ihm alle Reiche der Welt und ihre Herrlichkeit und sprach zu ihm: Das alles will ich dir geben, wenn du niederfällst und mich anbetest. Da sprach Jesus zu ihm: Weg mit dir, Satan!', 'Matthäus 4,8-10'),
('2025-10-21', 'Dienstag', NULL, 'Abram zog aus, wie der HERR zu ihm gesagt hatte.', '1. Mose 12,4', 'So hat Gott auch uns berufen, nicht allein aus den Juden, sondern auch aus den Heiden.', 'Römer 9,24'),
('2025-10-22', 'Mittwoch', NULL, 'So spricht der HERR: Gleichwie ich über dies Volk all dies große Unheil habe kommen lassen, so will ich auch alles Gute über sie kommen lassen, das ich ihnen zugesagt habe.', 'Jeremia 32,42', 'Unsre Hoffnung steht fest für euch, weil wir wissen: Wie ihr an den Leiden teilhabt, so habt ihr auch am Trost teil.', '2. Korinther 1,7'),
('2025-10-23', 'Donnerstag', NULL, 'Bringt eine Mutter es fertig, ihren Säugling zu vergessen? Hat sie nicht Mitleid mit dem Kind, das sie in ihrem Leib getragen hat? Und selbst wenn sie es vergessen könnte, ich vergesse euch nicht!', 'Jesaja 49,15', 'Gott ist Liebe.', '1. Johannes 4,8'),
('2025-10-24', 'Freitag', NULL, 'Es gibt eine Hoffnung für deine Zukunft, spricht der HERR.', 'Jeremia 31,17', 'Gott wollte in den kommenden Zeiten den überwältigenden Reichtum seiner Gnade zeigen durch die Güte, die er uns erweist in Christus Jesus.', 'Epheser 2,7'),
('2025-10-25', 'Samstag', NULL, 'Ihr sollt Brot die Fülle haben und sollt sicher in eurem Lande wohnen.', '3. Mose 26,5', 'Gottes Brot ist dasjenige, das vom Himmel herabkommt und der Welt Leben gibt.', 'Johannes 6,33'),
('2025-10-26', 'Sonntag', '19. Sonntag nach Trinitatis', 'Weigere dich nicht, dem Bedürftigen Gutes zu tun, wenn deine Hand es vermag.', 'Sprüche 3,27', 'Wenn in eure Versammlung ein Mann kommt mit einem goldenen Ring und in herrlicher Kleidung, es kommt aber auch ein Armer in unsauberer Kleidung, und ihr seht auf den, der herrlich gekleidet ist, und sprecht zu ihm: Setz du dich hierher auf den guten Platz!, und sprecht zu dem Armen: Stell du dich dorthin!, oder: Setz dich unten zu meinen Füßen!, macht ihr dann nicht Unterschiede unter euch und urteilt mit bösen Gedanken?', 'Jakobus 2,2-4'),
('2025-10-27', 'Montag', NULL, 'Aller Augen warten auf dich, und du gibst ihnen ihre Speise zur rechten Zeit. Du tust deine Hand auf und sättigst alles, was lebt, mit Wohlgefallen.', 'Psalm 145,15-16', 'Wer zwei Hemden hat, der gebe dem, der keines hat; und wer Speise hat, tue ebenso.', 'Lukas 3,11'),
('2025-10-28', 'Dienstag', NULL, 'Wer zugrunde gehen soll, der wird zuvor stolz; und Hochmut kommt vor dem Fall.', 'Sprüche 16,18', 'Jesus sprach: Was aus dem Menschen herauskommt, das macht den Menschen unrein.', 'Markus 7,20'),
('2025-10-29', 'Mittwoch', NULL, '/Der HERR spricht:/ Werdet ihr meiner Stimme gehorchen und meinen Bund halten, so sollt ihr mein Eigentum sein vor allen Völkern; denn die ganze Erde ist mein.', '2. Mose 19,5', 'Wenn ihr meine Gebote haltet, bleibt ihr in meiner Liebe, so wie ich meines Vaters Gebote gehalten habe und bleibe in seiner Liebe.', 'Johannes 15,10'),
('2025-10-30', 'Donnerstag', NULL, 'Wende dich, HERR, und errette meine Seele, hilf mir um deiner Güte willen!', 'Psalm 6,5', 'Gott tröstet uns in aller unserer Bedrängnis, damit wir auch trösten können, die in allerlei Bedrängnis sind, mit dem Trost, mit dem wir selber getröstet werden von Gott.', '2. Korinther 1,4'),
('2025-10-31', 'Freitag', 'Reformationstag', 'Wie könnt ihr rechten mit mir? Ihr seid alle von mir abgefallen, spricht der HERR.', 'Jeremia 2,29', 'Es ist hier kein Unterschied: Sie sind allesamt Sünder und ermangeln des Ruhmes, den sie vor Gott haben sollen, und werden ohne Verdienst gerecht aus seiner Gnade durch die Erlösung, die durch Christus Jesus geschehen ist.', 'Römer 3,22-24'),
('2025-11-01', 'Samstag', NULL, 'Wenn ich schaue allein auf deine Gebote, so werde ich nicht zuschanden.', 'Psalm 119,6', 'Das aber auf dem guten Land sind die, die das Wort hören und behalten in einem feinen, guten Herzen und bringen Frucht in Geduld.', 'Lukas 8,15'),
('2025-11-02', 'Sonntag', '20. Sonntag nach Trinitatis', 'Es sollen viele Völker sich zum HERRN wenden und sollen mein Volk sein.', 'Sacharja 2,15', 'Gott wird bei ihnen wohnen, und sie werden seine Völker sein, und er selbst, Gott mit ihnen, wird ihr Gott sein.', 'Offenbarung 21,3'),
('2025-11-03', 'Montag', NULL, 'Siehe, auch jetzt noch ist mein Zeuge im Himmel, und mein Fürsprecher ist in der Höhe.', 'Hiob 16,19', 'Weil wir denn einen großen Hohenpriester haben, Jesus, den Sohn Gottes, der die Himmel durchschritten hat, so lasst uns festhalten an dem Bekenntnis.', 'Hebräer 4,14'),
('2025-11-04', 'Dienstag', NULL, 'Ich werde wandeln vor dem HERRN im Lande der Lebendigen.', 'Psalm 116,9', 'Betrachtet euch als solche, die für die Sünde tot, für Gott aber lebendig sind, in Christus Jesus.', 'Römer 6,11'),
('2025-11-05', 'Mittwoch', NULL, 'Lasst uns unsere Wege prüfen und erforschen, und lasst uns zurückkehren zum HERRN!', 'Klagelieder 3,40', 'Ich bete darum, dass eure Liebe immer noch reicher werde an Erkenntnis und aller Erfahrung, sodass ihr prüfen könnt, was das Beste sei, damit ihr lauter und unanstößig seid für den Tag Christi.', 'Philipper 1,9-10'),
('2025-11-06', 'Donnerstag', NULL, 'Um meines Namens willen halte ich meinen Zorn zurück, und um meines Ruhmes willen bezähme ich mich zu deinen Gunsten, dass ich dich nicht ausrotte.', 'Jesaja 48,9', 'Maria wird einen Sohn gebären, dem sollst du den Namen Jesus geben, denn er wird sein Volk retten von ihren Sünden.', 'Matthäus 1,21'),
('2025-11-07', 'Freitag', NULL, 'Lass deiner sich freuen und fröhlich sein alle, die nach dir fragen.', 'Psalm 40,17', 'Gelobt sei, der da kommt, der König, in dem Namen des Herrn! Friede sei im Himmel und Ehre in der Höhe!', 'Lukas 19,38'),
('2025-11-08', 'Samstag', NULL, 'Die Blinden will ich auf dem Wege leiten, den sie nicht wissen; ich will sie führen auf den Steigen, die sie nicht kennen.', 'Jesaja 42,16', 'Jesus sprach: Ich bin das Licht der Welt. Wer mir nachfolgt, der wird nicht wandeln in der Finsternis, sondern wird das Licht des Lebens haben.', 'Johannes 8,12'),
('2025-11-09', 'Sonntag', 'Drittletzter Sonntag des Kirchenjahres', 'So hütet euch nun, dass ihr den Bund des HERRN, eures Gottes, nicht vergesst, den er mit euch geschlossen hat, und nicht ein Bildnis macht von irgendeiner Gestalt, wie es der HERR, dein Gott, geboten hat.', '5. Mose 4,23', 'Gottes unsichtbares Wesen – das ist seine ewige Kraft und Gottheit – wird seit der Schöpfung der Welt, wenn man es mit Vernunft wahrnimmt, an seinen Werken ersehen.', 'Römer 1,20'),
('2025-11-10', 'Montag', NULL, 'Warum willst du mit Gott hadern, weil er auf Menschenworte nicht Antwort gibt? Denn auf eine Weise redet Gott und auf eine zweite; nur beachtet man’s nicht.', 'Hiob 33,13-14', 'Wer Ohren hat, der höre, was der Geist den Gemeinden sagt!', 'Offenbarung 2,7'),
('2025-11-11', 'Dienstag', NULL, 'Fürchte dich nicht und verzage nicht!', 'Josua 8,1', 'Da berührte Jesus ihre Augen und sprach: Euch geschehe nach eurem Glauben! Und ihre Augen wurden geöffnet.', 'Matthäus 9,29-30'),
('2025-11-12', 'Mittwoch', NULL, 'Ihr sollt richten ohne Ansehen der Person, den Kleinen sollt ihr anhören wie den Großen, und ihr sollt euch vor niemandem fürchten, denn es ist Gottes Gericht.', '5. Mose 1,17', 'Wenn ihr aber nach dem Ansehen der Person urteilt, dann begeht ihr eine Sünde und werdet überführt vom Gesetz als seine Übertreter.', 'Jakobus 2,9'),
('2025-11-13', 'Donnerstag', NULL, 'HERR, ich freue mich über deine Hilfe.', '1. Samuel 2,1', 'Gott sei Dank, der uns den Sieg gibt durch unsern Herrn Jesus Christus!', '1. Korinther 15,57'),
('2025-11-14', 'Freitag', NULL, 'Wie ein Adler ausführt seine Jungen und über ihnen schwebt, so breitete der HERR seine Fittiche aus und nahm sein Volk und trug es auf seinen Flügeln.', '5. Mose 32,11', 'Mein Vater, der sie mir anvertraut hat, ist mächtiger als alle. Niemand kann sie aus seiner Hand reißen.', 'Johannes 10,29'),
('2025-11-15', 'Samstag', NULL, 'Fülle uns frühe mit deiner Gnade, so wollen wir rühmen und fröhlich sein unser Leben lang.', 'Psalm 90,14', 'Das Ziel der Unterweisung ist Liebe aus reinem Herzen und aus gutem Gewissen und aus ungeheucheltem Glauben.', '1. Timotheus 1,5'),
('2025-11-16', 'Sonntag', 'Vorletzter Sonntag des Kirchenjahres', 'Der HERR sah ihre Not an, als er ihre Klage hörte, und gedachte um ihretwillen an seinen Bund.', 'Psalm 106,44-45', 'Und sie steinigten Stephanus; der rief den Herrn an und sprach: Herr Jesus, nimm meinen Geist auf!', 'Apostelgeschichte 7,59'),
('2025-11-17', 'Montag', NULL, 'Die Wege des HERRN sind lauter Güte und Treue für alle, die seinen Bund und seine Zeugnisse halten.', 'Psalm 25,10', 'Selig seid ihr, wenn euch die Menschen um meinetwillen schmähen und verfolgen und allerlei Böses gegen euch reden und dabei lügen. Seid fröhlich und jubelt; es wird euch im Himmel reichlich belohnt werden.', 'Matthäus 5,11-12'),
('2025-11-18', 'Dienstag', NULL, 'Der HERR ist ein Schild allen, die ihm vertrauen.', 'Psalm 18,31', 'Wo keine Hoffnung war, hat Abraham auf Hoffnung hin geglaubt, auf dass er der Vater vieler Völker werde.', 'Römer 4,18'),
('2025-11-19', 'Mittwoch', 'Buss- und Bettag', 'Weh denen, die Unheil planen, weil sie die Macht haben!', 'Micha 2,1', 'Jesus rief die Jünger zu sich und sprach: Ihr wisst, dass die Herrscher ihre Völker niederhalten und die Mächtigen ihnen Gewalt antun. So soll es nicht sein unter euch.', 'Matthäus 20,25-26'),
('2025-11-20', 'Donnerstag', NULL, 'Gott der HERR machte den Menschen aus Staub von der Erde und blies ihm den Odem des Lebens in seine Nase. Und so ward der Mensch ein lebendiges Wesen.', '1. Mose 2,7', 'Nicht mehr ich lebe, sondern Christus lebt in mir; sofern ich jetzt noch im Fleisch lebe, lebe ich im Glauben an den Sohn Gottes, der mich geliebt und sich für mich hingegeben hat.', 'Galater 2,20'),
('2025-11-21', 'Freitag', NULL, 'HERR, du bist Gott, und deine Worte sind Wahrheit.', '2. Samuel 7,28', 'Wenn ihr bleiben werdet an meinem Wort, so seid ihr wahrhaftig meine Jünger und werdet die Wahrheit erkennen, und die Wahrheit wird euch frei machen.', 'Johannes 8,31-32'),
('2025-11-22', 'Samstag', NULL, 'Ich bin gnädig, spricht der HERR, und will nicht ewiglich zürnen. Allein erkenne deine Schuld, dass du wider den HERRN, deinen Gott, gesündigt hast.', 'Jeremia 3,12-13', 'Vergib uns unsere Schuld, wie auch wir vergeben haben jenen, die an uns schuldig geworden sind.', 'Matthäus 6,12'),
('2025-11-23', 'Sonntag', 'Letzter Sonntag des Kirchenjahres – Ewigkeitssonntag', 'Der HERR spricht: Ich habe dich zum Licht der Völker gemacht, dass mein Heil reiche bis an die Enden der Erde.', 'Jesaja 49,6', 'Paulus sah eine Erscheinung bei Nacht: Ein Mann aus Makedonien stand da und bat ihn: Komm herüber nach Makedonien und hilf uns! Als er aber die Erscheinung gesehen hatte, da suchten wir sogleich nach Makedonien zu reisen, gewiss, dass uns Gott dahin berufen hatte, ihnen das Evangelium zu predigen.', 'Apostelgeschichte 16,9-10'),
('2025-11-24', 'Montag', NULL, '/König Nebukadnezar sprach:/ Es gefällt mir, die Zeichen und Wunder zu verkünden, die der höchste Gott an mir getan hat.', 'Daniel 3,32', 'Wer nicht gegen uns ist, der ist für uns.', 'Markus 9,40'),
('2025-11-25', 'Dienstag', NULL, 'Der HERR wird’s vollenden um meinetwillen.', 'Psalm 138,8', 'Nachdem Jesus nun das Ziel erreicht hat, ist er für alle, die ihm gehorchen, zum Begründer ihrer endgültigen Rettung geworden.', 'Hebräer 5,9'),
('2025-11-26', 'Mittwoch', NULL, 'HERR, deine Ratschlüsse von alters her sind treu und wahrhaftig.', 'Jesaja 25,1', 'Lasst uns festhalten an dem Bekenntnis der Hoffnung und nicht wanken; denn er ist treu, der sie verheißen hat.', 'Hebräer 10,23'),
('2025-11-27', 'Donnerstag', NULL, '/Gott spricht:/ Als du mich in der Not anriefst, half ich dir heraus.', 'Psalm 81,8', 'Als Petrus den starken Wind sah, erschrak er und begann zu sinken und schrie: Herr, rette mich! Jesus aber streckte sogleich die Hand aus und ergriff ihn.', 'Matthäus 14,30-31'),
('2025-11-28', 'Freitag', NULL, 'Wie der neue Himmel und die neue Erde, die ich mache, vor mir Bestand haben, spricht der HERR, so soll auch euer Geschlecht und Name Bestand haben.', 'Jesaja 66,22', 'Nathanael antwortete Jesus: Rabbi, du bist Gottes Sohn, du bist der König von Israel!', 'Johannes 1,49'),
('2025-11-29', 'Samstag', NULL, 'Ein Tag in deinen Vorhöfen ist besser als sonst tausend.', 'Psalm 84,11', 'Jesus sagte zu seinen Eltern: Warum habt ihr mich gesucht? Wusstet ihr nicht, dass ich im Haus meines Vaters sein muss?', 'Lukas 2,49'),
('2025-11-30', 'Sonntag', '1. Sonntag im Advent', 'Es wird keiner den andern noch ein Bruder den andern lehren und sagen: »Erkenne den HERRN«, denn sie sollen mich alle erkennen, beide, Klein und Groß, spricht der HERR.', 'Jeremia 31,34', 'Als aber die Hohenpriester und Schriftgelehrten die Wunder sahen, die Jesus tat, und die Kinder, die im Tempel schrien und sagten: Hosianna dem Sohn Davids!, entrüsteten sie sich und sprachen zu ihm: Hörst du auch, was diese sagen? Jesus sprach zu ihnen: Ja! Habt ihr nie gelesen: Aus dem Munde der Unmündigen und Säuglinge hast du dir Lob bereitet?', 'Matthäus 21,15-16'),
('2025-12-01', 'Montag', NULL, 'Sende dein Licht und deine Wahrheit, dass sie mich leiten zu deiner Wohnung.', 'Psalm 43,3', 'Herr, nun lässt du deinen Diener in Frieden fahren, wie du gesagt hast; denn meine Augen haben deinen Heiland gesehen, das Heil, das du bereitet hast vor allen Völkern, ein Licht zur Erleuchtung der Heiden und zum Preis deines Volkes Israel.', 'Lukas 2,29-32'),
('2025-12-02', 'Dienstag', NULL, 'Bekehre du mich, so will ich mich bekehren; denn du, HERR, bist mein Gott!', 'Jeremia 31,18', 'Wir predigen euch das Evangelium, dass ihr euch bekehren sollt von diesen nichtigen Göttern zu dem lebendigen Gott, der Himmel und Erde und das Meer und alles, was darin ist, gemacht hat.', 'Apostelgeschichte 14,15'),
('2025-12-03', 'Mittwoch', NULL, 'Wollte Gott, dass alle im Volk des HERRN Propheten wären und der HERR seinen Geist über sie kommen ließe!', '4. Mose 11,29', 'Ihr werdet die Kraft des Heiligen Geistes empfangen, der auf euch kommen wird.', 'Apostelgeschichte 1,8'),
('2025-12-04', 'Donnerstag', NULL, 'Kinder, die das Gesetz nicht kennen, sollen es auch hören und lernen, den HERRN, euren Gott, zu fürchten alle Tage.', '5. Mose 31,13', 'Ihr Väter, reizt eure Kinder nicht zum Zorn, sondern lasst sie aufwachsen in der Erziehung und Zurechtweisung des Herrn.', 'Epheser 6,4'),
('2025-12-05', 'Freitag', NULL, 'HERR, zeige uns deine Gnade und gib uns dein Heil!', 'Psalm 85,8', 'Das Heil kommt von den Juden.', 'Johannes 4,22'),
('2025-12-06', 'Samstag', NULL, 'O HERR, hilf! O HERR, lass wohlgelingen!', 'Psalm 118,25', 'Der heilige Geist sprach: Bestimmt mir den Barnabas und den Saulus für das Werk, zu dem ich sie berufen habe. Da fasteten und beteten sie, legten ihnen die Hände auf und ließen sie gehen.', 'Apostelgeschichte 13,2-3'),
('2025-12-07', 'Sonntag', '2. Sonntag im Advent', 'Der HERR richtet auf, die niedergeschlagen sind.', 'Psalm 146,8', 'Wahrlich, wahrlich, ich sage euch: Ihr werdet weinen und klagen, aber die Welt wird sich freuen; ihr werdet traurig sein, doch eure Traurigkeit soll zur Freude werden.', 'Johannes 16,20'),
('2025-12-08', 'Montag', NULL, 'Du wirst mit deinem Gott zurückkehren. Halte fest an Liebe und Recht und hoffe stets auf deinen Gott!', 'Hosea 12,7', 'Christus Jesus ist in die Welt gekommen, um Sünder zu retten – unter ihnen bin ich der erste. Doch eben darum habe ich Erbarmen gefunden: An mir als Erstem sollte Christus Jesus die ganze Fülle seiner Geduld zeigen, beispielhaft für alle, die künftig an ihn glauben und so ewiges Leben finden.', '1. Timotheus 1,15-16'),
('2025-12-09', 'Dienstag', NULL, 'Ich will euch tragen, bis ihr grau werdet. Ich habe es getan; ich will heben und tragen und erretten.', 'Jesaja 46,4', 'Unterstütze die Witwen, die alleinstehend sind.', '1. Timotheus 5,3'),
('2025-12-10', 'Mittwoch', NULL, 'Du bist groß, Herr HERR! Denn es ist keiner wie du, und ist kein Gott außer dir nach allem, was wir mit unsern Ohren gehört haben.', '2. Samuel 7,22', 'Ich bin das A und das O, der Erste und der Letzte, der Anfang und das Ende.', 'Offenbarung 22,13'),
('2025-12-11', 'Donnerstag', NULL, 'Ich habe dich je und je geliebt, darum habe ich dich zu mir gezogen aus lauter Güte.', 'Jeremia 31,3', 'Also hat Gott die Welt geliebt, dass er seinen eingeborenen Sohn gab, auf dass alle, die an ihn glauben, nicht verloren werden, sondern das ewige Leben haben.', 'Johannes 3,16'),
('2025-12-12', 'Freitag', NULL, 'Ach dass du den Himmel zerrissest und führest herab!', 'Jesaja 63,19', 'Siehe, du wirst schwanger werden und einen Sohn gebären, dem sollst du den Namen Jesus geben. Der wird groß sein und Sohn des Höchsten genannt werden.', 'Lukas 1,31-32'),
('2025-12-13', 'Samstag', NULL, 'Siehe, es kommt die Zeit, spricht der HERR, dass die Stadt für den HERRN wieder gebaut werden wird.', 'Jeremia 31,38', 'Und ich sah die heilige Stadt: das neue Jerusalem. Sie kam von Gott aus dem Himmel herab – für die Hochzeit bereit wie eine Braut, die sich für ihren Mann geschmückt hat. Und ich hörte eine laute Stimme vom Thron her rufen: Sieh her: Gottes Wohnung ist bei den Menschen!', 'Offenbarung 21,2-3'),
('2025-12-14', 'Sonntag', '3. Sonntag im Advent', 'Wenn ich rufe zu dir, HERR, mein Fels, so schweige mir nicht.', 'Psalm 28,1', 'Sprich nur ein Wort, und mein Knecht wird gesund.', 'Lukas 7,7'),
('2025-12-15', 'Montag', NULL, 'Er ist um unsrer Missetat willen verwundet und um unsrer Sünde willen zerschlagen.', 'Jesaja 53,5', 'Gott hat auch seinen eigenen Sohn nicht verschont, sondern hat ihn für uns alle dahingegeben – wie sollte er uns mit ihm nicht alles schenken?', 'Römer 8,32'),
('2025-12-16', 'Dienstag', NULL, 'Sei nur stille zu Gott, meine Seele; denn er ist meine Hoffnung.', 'Psalm 62,6', 'Wir warten auf die selige Hoffnung und Erscheinung der Herrlichkeit des großen Gottes und unseres Heilands, Jesus Christus.', 'Titus 2,13'),
('2025-12-17', 'Mittwoch', NULL, 'Weh denen, die Böses gut und Gutes böse nennen, die aus Finsternis Licht und aus Licht Finsternis machen!', 'Jesaja 5,20', 'Lass dich nicht vom Bösen überwinden, sondern überwinde das Böse mit Gutem.', 'Römer 12,21'),
('2025-12-18', 'Donnerstag', NULL, 'Bessert euer Leben und euer Tun, so will ich euch wohnen lassen an diesem Ort.', 'Jeremia 7,3', 'Bemüht euch deshalb nach Kräften, dass zu eurem Glauben das richtige Verhalten kommt. Zum richtigen Verhalten soll die Erkenntnis kommen, zur Erkenntnis die Selbstbeherrschung, zur Selbstbeherrschung die Standhaftigkeit, zur Standhaftigkeit die Ausübung des Glaubens, zur Ausübung des Glaubens die geschwisterliche Liebe und zur geschwisterlichen Liebe die Liebe überhaupt.', '2. Petrus 1,5-7'),
('2025-12-19', 'Freitag', NULL, 'Es ist kein Fels, wie unser Gott ist.', '1. Samuel 2,2', 'Werdet stark durch eure Verbundenheit mit dem Herrn. Lasst euch stärken durch seine Kraft.', 'Epheser 6,10'),
('2025-12-20', 'Samstag', NULL, 'Josef tröstete seine Brüder und redete freundlich mit ihnen.', '1. Mose 50,21', 'Seid untereinander freundlich und herzlich und vergebt einer dem andern, wie auch Gott euch vergeben hat in Christus.', 'Epheser 4,32'),
('2025-12-21', 'Sonntag', '4. Sonntag im Advent', 'Meine Hand hat alles gemacht, was da ist, spricht der HERR. Ich sehe aber auf den Elenden und auf den, der zerbrochenen Geistes ist und der erzittert vor meinem Wort.', 'Jesaja 66,2', 'Als aber erschien die Freundlichkeit und Menschenliebe Gottes, unseres Heilands, machte er uns selig – nicht um der Werke willen, die wir in Gerechtigkeit getan hätten, sondern nach seiner Barmherzigkeit.', 'Titus 3,4-5'),
('2025-12-22', 'Montag', NULL, 'Weh dem, der sein Gut mehrt mit fremdem Gut – wie lange wird’s währen?', 'Habakuk 2,6', 'Wer im Geringsten treu ist, der ist auch im Großen treu; und wer im Geringsten ungerecht ist, der ist auch im Großen ungerecht.', 'Lukas 16,10'),
('2025-12-23', 'Dienstag', NULL, 'Sei getrost, alles Volk im Lande, spricht der HERR, und arbeitet! Denn ich bin mit euch.', 'Haggai 2,4', 'Wer da kärglich sät, der wird auch kärglich ernten; und wer da sät im Segen, der wird auch ernten im Segen.', '2. Korinther 9,6'),
('2025-12-24', 'Mittwoch', 'Heiliger Abend', 'Gerechtigkeit und Recht sind deines Thrones Stütze, Gnade und Treue treten vor dein Angesicht.', 'Psalm 89,15', 'Das Wort ward Fleisch und wohnte unter uns, und wir sahen seine Herrlichkeit, eine Herrlichkeit als des eingeborenen Sohnes vom Vater, voller Gnade und Wahrheit.', 'Johannes 1,14'),
('2025-12-25', 'Donnerstag', 'Weihnachtsfest', 'Bringe uns, HERR, zu dir zurück, dass wir wieder heimkommen; erneure unsre Tage wie vor alters!', 'Klagelieder 5,21', 'Euch ist heute der Heiland geboren, welcher ist Christus, der Herr, in der Stadt Davids.', 'Lukas 2,11'),
('2025-12-26', 'Freitag', '2. Weihnachtstag', 'Die Gebote des HERRN sind lauter und erleuchten die Augen.', 'Psalm 19,9', 'Alle, die im Hohen Rat saßen, blickten auf Stephanus und sahen sein Angesicht wie eines Engels Angesicht.', 'Apostelgeschichte 6,15'),
('2025-12-27', 'Samstag', NULL, 'Ich gehe einher in der Kraft Gottes des HERRN; ich preise deine Gerechtigkeit allein.', 'Psalm 71,16', 'Denen aber, die berufen sind, Juden und Griechen, predigen wir Christus als Gottes Kraft und Gottes Weisheit.', '1. Korinther 1,24'),
('2025-12-28', 'Sonntag', '1. Sonntag nach Weihnachten', 'Siehe, wie fein und lieblich ist’s, wenn Brüder einträchtig beieinander wohnen! Denn dort verheißt der HERR Segen und Leben bis in Ewigkeit.', 'Psalm 133,1.3', 'Ehre sei Gott in der Höhe und Friede auf Erden bei den Menschen seines Wohlgefallens.', 'Lukas 2,14'),
('2025-12-29', 'Montag', NULL, 'Du, HERR, sei nicht ferne; meine Stärke, eile, mir zu helfen!', 'Psalm 22,20', 'Die Kraft des Herrn war mit Jesus, dass er heilen konnte.', 'Lukas 5,17'),
('2025-12-30', 'Dienstag', NULL, 'Der HERR sprach zu Mose: Du hast Gnade vor meinen Augen gefunden, und ich kenne dich mit Namen.', '2. Mose 33,17', 'Das Fundament Gottes wankt nicht, und es trägt das Siegel mit der Aufschrift: #Der Herr kennt die Seinen.#', '2. Timotheus 2,19'),
('2025-12-31', 'Mittwoch', 'Altjahresabend', 'Der HERR schafft deinen Grenzen Frieden.', 'Psalm 147,14', 'Selig sind, die Frieden stiften; denn sie werden Gottes Kinder heißen.', 'Matthäus 5,9');
